import React, { useEffect, useState } from "react";
import { DataGrid } from "@mui/x-data-grid";
import {
  Box,
  Drawer,
  Typography,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stack,
} from "@mui/material";
import ProductFormDialog from "./ProductFormDialog";
import CustomizeColumnsDialog from "./CustomizeColumnsDialog";
import { fetchProducts, createProduct } from "../api";
import { sendIndividualEmails, sendGroupEmail } from "../emailSender";

// The sidebar drawer width
const drawerWidth = 300;

// Base columns for the DataGrid
const baseColumns = [
  { field: "title", headerName: "Title", width: 200 },
  { field: "amazon_url", headerName: "Amazon URL", width: 200 },
  { field: "asin", headerName: "ASIN", width: 120 },
  { field: "price", headerName: "Price", width: 120, type: "number" },
  { field: "moq", headerName: "MOQ", width: 100, type: "number" },
  { field: "qty", headerName: "Quantity", width: 100, type: "number" },
  { field: "upc", headerName: "UPC", width: 120 },
  { field: "cost", headerName: "Cost", width: 120, type: "number" },
  { field: "vendor", headerName: "Vendor", width: 150 },
  { field: "lead_time", headerName: "Lead Time", width: 150 },
  { field: "exp_date", headerName: "Exp Date", width: 150 },
  { field: "fob", headerName: "FOB", width: 150 },
  { field: "vendor_id", headerName: "Vendor ID", width: 150 },
  { field: "profit_moq", headerName: "Profit MOQ", width: 150, type: "number" },
  { field: "image_url", headerName: "Image URL", width: 200 },
  { field: "category", headerName: "Category", width: 150 },
  { field: "out_of_stock", headerName: "Out of Stock", width: 150, type: "boolean" },
  { field: "walmart_url", headerName: "Walmart URL", width: 200 },
  { field: "ebay_url", headerName: "eBay URL", width: 200 },
];

const InternalProductList = ({ onBack }) => {
  // ===================
  // 1. DATA & FILTER STATES
  // ===================
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  // Example filter states (uncomment or modify for your actual logic)
  const [categoryFilter, setCategoryFilter] = useState("");
  const [vendorFilter, setVendorFilter] = useState("");
  // If you had costRange or stockFilter, add them here:
  // const [costRange, setCostRange] = useState([0, 1000000]);
  // const [stockFilter, setStockFilter] = useState("in");

  // For the DataGrid selection
  const [selectedIds, setSelectedIds] = useState([]);

  // Dialog states
  const [dialogOpen, setDialogOpen] = useState(false);
  const [customizeDialogOpen, setCustomizeDialogOpen] = useState(false);

  // ===================
  // 2. COLUMN VISIBILITY
  // ===================
  // We'll store a visibility model in localStorage so the user’s preferences persist
  const [columnVisibilityModel, setColumnVisibilityModel] = useState({});

  // On mount, try to load the saved visibility from local storage
  useEffect(() => {
    const savedModel = localStorage.getItem("columnVisibilityModel");
    if (savedModel) {
      setColumnVisibilityModel(JSON.parse(savedModel));
    } else {
      // If no saved model, default everything to true
      const defaultModel = {};
      baseColumns.forEach((col) => {
        defaultModel[col.field] = true;
      });
      setColumnVisibilityModel(defaultModel);
    }
  }, []);

  // When column visibility changes, update state + localStorage
  const handleColumnVisibilityChange = (newModel) => {
    setColumnVisibilityModel(newModel);
    localStorage.setItem("columnVisibilityModel", JSON.stringify(newModel));
  };

  // ===================
  // 3. LOAD PRODUCTS ON MOUNT
  // ===================
  useEffect(() => {
    const loadProducts = async () => {
      try {
        const data = await fetchProducts();
        // data might be { products: [] } or an array
        const productData = data.products || data;
        setProducts(productData);
        setFilteredProducts(productData);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };
    loadProducts();
  }, []);

  // ===================
  // 4. FILTER LOGIC
  // ===================
  const applyFilters = () => {
    let temp = [...products];

    if (categoryFilter) {
      temp = temp.filter((p) => String(p.category).toLowerCase() === categoryFilter.toLowerCase());
    }
    if (vendorFilter) {
      temp = temp.filter((p) => String(p.vendor).toLowerCase() === vendorFilter.toLowerCase());
    }
    // If you have costRange or stockFilter, add them here:
    // if (costRange.length === 2) {
    //   const [minCost, maxCost] = costRange;
    //   temp = temp.filter((p) => p.cost >= minCost && p.cost <= maxCost);
    // }
    // if (stockFilter === "in") {
    //   temp = temp.filter((p) => !p.out_of_stock);
    // } else if (stockFilter === "out") {
    //   temp = temp.filter((p) => p.out_of_stock);
    // }

    setFilteredProducts(temp);
  };

  const resetFilters = () => {
    setCategoryFilter("");
    setVendorFilter("");
    // setCostRange([0, 1000000]);
    // setStockFilter("in");
    setFilteredProducts(products);
  };

  // ===================
  // 5. HANDLE SELECTION
  // ===================
  const handleSelectionModelChange = (newSelection) => {
    console.log("Selected IDs:", newSelection);
    setSelectedIds(newSelection);
  };

  // ===================
  // 6. EMAIL HANDLERS
  // ===================
  // We need full product objects for the email code
  const handleSendIndividualEmails = () => {
    const selectedProducts = filteredProducts.filter((p) =>
      selectedIds.includes(String(p.id))
    );
    sendIndividualEmails(selectedProducts);
  };

  const handleSendGroupEmail = () => {
    const selectedProducts = filteredProducts.filter((p) =>
      selectedIds.includes(String(p.id))
    );
    sendGroupEmail(selectedProducts);
  };

  // ===================
  // 7. MERGE COLUMNS + VISIBILITY
  // ===================
  // The DataGrid can read a `columnVisibilityModel` prop:
  // { fieldName: true/false }
  // but we must pass them in as a separate prop
  const columns = [...baseColumns];

  return (
    <Box sx={{ display: "flex" }}>
      {/* ================= Drawer for Filters + Actions ================= */}
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: {
            width: drawerWidth,
            boxSizing: "border-box",
          },
        }}
      >
        <Box sx={{ p: 2 }}>
          {onBack && (
            <Button variant="contained" onClick={onBack} sx={{ mb: 2 }}>
              Back to Public View
            </Button>
          )}
          <Typography variant="h6" gutterBottom>
            Filters & Actions
          </Typography>

          {/* Example filter controls */}
          <Stack spacing={1} sx={{ mb: 2 }}>
            <FormControl size="small" fullWidth>
              <InputLabel>Category</InputLabel>
              <Select
                value={categoryFilter}
                label="Category"
                onChange={(e) => setCategoryFilter(e.target.value)}
              >
                <MenuItem value="">(All)</MenuItem>
                <MenuItem value="Books">Books</MenuItem>
                <MenuItem value="Electronics">Electronics</MenuItem>
                {/* Add more categories as needed */}
              </Select>
            </FormControl>

            <FormControl size="small" fullWidth>
              <InputLabel>Vendor</InputLabel>
              <Select
                value={vendorFilter}
                label="Vendor"
                onChange={(e) => setVendorFilter(e.target.value)}
              >
                <MenuItem value="">(All)</MenuItem>
                <MenuItem value="Acme">Acme</MenuItem>
                <MenuItem value="Global Co">Global Co</MenuItem>
                {/* Add more vendors as needed */}
              </Select>
            </FormControl>

            <Button variant="contained" onClick={applyFilters}>
              Apply Filters
            </Button>
            <Button variant="outlined" onClick={resetFilters}>
              Reset Filters
            </Button>
          </Stack>

          {/* Actions */}
          <Stack spacing={1}>
            <Button variant="contained" onClick={() => setDialogOpen(true)}>
              Add Product
            </Button>
            <Button
              variant="contained"
              color="info"
              onClick={() => setCustomizeDialogOpen(true)}
            >
              Customize Columns
            </Button>
            <Button variant="contained" color="success" onClick={handleSendIndividualEmails}>
              Send Individual Emails
            </Button>
            <Button variant="contained" color="success" onClick={handleSendGroupEmail}>
              Send Group Email
            </Button>
          </Stack>
        </Box>
      </Drawer>

      {/* ================= Main Content ================= */}
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Internal Product List
        </Typography>
        <div style={{ height: 600, width: "100%" }}>
          <DataGrid
            rows={filteredProducts}
            columns={columns}
            getRowId={(row) => String(row.id)}
            checkboxSelection
            selectionModel={selectedIds}
            onSelectionModelChange={handleSelectionModelChange}
            pageSize={10}
            rowsPerPageOptions={[5, 10, 25, 50]}
            // Visibility model from our state
            columnVisibilityModel={columnVisibilityModel}
            // If you want to handle changes in the UI (e.g. show/hide columns from the Column menu):
            // onColumnVisibilityModelChange={handleColumnVisibilityChange}
          />
        </div>
      </Box>

      {/* ================= Add Product Dialog ================= */}
      <ProductFormDialog
        open={dialogOpen}
        onClose={() => setDialogOpen(false)}
        onSubmit={(newProduct) => {
          createProduct(newProduct);
          setDialogOpen(false);
        }}
      />

      {/* ================= Customize Columns Dialog ================= */}
      <CustomizeColumnsDialog
        open={customizeDialogOpen}
        onClose={() => setCustomizeDialogOpen(false)}
        columns={baseColumns}
        columnVisibilityModel={columnVisibilityModel}
        onColumnVisibilityChange={handleColumnVisibilityChange}
      />
    </Box>
  );
};

export default InternalProductList;
