import React, { useEffect, useState } from "react";
import axios from "axios";
import { DataGrid } from "@mui/x-data-grid";
import {
  Box,
  Drawer,
  Typography,
  Button,
  Slider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";

const drawerWidth = 300;

// Define columns for all product fields
const columns = [
  { field: "id", headerName: "ID", width: 70 },
  { field: "title", headerName: "Title", width: 200 },
  { field: "amazon_url", headerName: "Amazon URL", width: 200 },
  { field: "asin", headerName: "ASIN", width: 120 },
  { field: "price", headerName: "Price", width: 120, type: "number" },
  { field: "moq", headerName: "MOQ", width: 100, type: "number" },
  { field: "qty", headerName: "Quantity", width: 100, type: "number" },
  { field: "upc", headerName: "UPC", width: 120 },
  { field: "cost", headerName: "Cost", width: 120, type: "number" },
  { field: "vendor", headerName: "Vendor", width: 150 },
  { field: "lead_time", headerName: "Lead Time", width: 150 },
  { field: "exp_date", headerName: "Exp Date", width: 150 },
  { field: "fob", headerName: "FOB", width: 150 },
  { field: "profit_moq", headerName: "Profit MOQ", width: 150, type: "number" },
  { field: "image_url", headerName: "Image URL", width: 200 },
  { field: "category", headerName: "Category", width: 150 },
  { field: "out_of_stock", headerName: "Out of Stock", width: 150, type: "boolean" },
  { field: "walmart_url", headerName: "Walmart URL", width: 200 },
  { field: "ebay_url", headerName: "eBay URL", width: 200 },
];

const InternalProductList = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  // Filter state
  const [categoryFilter, setCategoryFilter] = useState("");
  const [vendorFilter, setVendorFilter] = useState("");
  const [costRange, setCostRange] = useState([0, 1000]);

  // Fetch products on mount
  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get("http://127.0.0.1:8000/products/");
        const data = response.data.products || response.data;
        setProducts(data);
        setFilteredProducts(data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    }
    fetchData();
  }, []);

  // Apply filters based on selected options
  const applyFilters = () => {
    let filtered = products;
    if (categoryFilter) {
      filtered = filtered.filter(
        (product) => product.category === categoryFilter
      );
    }
    if (vendorFilter) {
      filtered = filtered.filter(
        (product) => product.vendor === vendorFilter
      );
    }
    if (costRange && costRange.length === 2) {
      filtered = filtered.filter(
        (product) =>
          product.cost >= costRange[0] && product.cost <= costRange[1]
      );
    }
    setFilteredProducts(filtered);
  };

  // Reset all filters
  const resetFilters = () => {
    setCategoryFilter("");
    setVendorFilter("");
    setCostRange([0, 1000]);
    setFilteredProducts(products);
  };

  // Derive unique options for filters from products data
  const uniqueCategories = [
    ...new Set(products.map((p) => p.category).filter(Boolean)),
  ];
  const uniqueVendors = [
    ...new Set(products.map((p) => p.vendor).filter(Boolean)),
  ];

  return (
    <Box sx={{ display: "flex" }}>
      {/* Sidebar Filters */}
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: "border-box" },
        }}
      >
        <Box sx={{ p: 2 }}>
          <Typography variant="h6" gutterBottom>
            Filters
          </Typography>
          <FormControl fullWidth sx={{ mb: 2 }}>
            <InputLabel id="category-select-label">Category</InputLabel>
            <Select
              labelId="category-select-label"
              value={categoryFilter}
              label="Category"
              onChange={(e) => setCategoryFilter(e.target.value)}
            >
              <MenuItem value="">
                <em>All</em>
              </MenuItem>
              {uniqueCategories.map((cat) => (
                <MenuItem key={cat} value={cat}>
                  {cat}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <FormControl fullWidth sx={{ mb: 2 }}>
            <InputLabel id="vendor-select-label">Vendor</InputLabel>
            <Select
              labelId="vendor-select-label"
              value={vendorFilter}
              label="Vendor"
              onChange={(e) => setVendorFilter(e.target.value)}
            >
              <MenuItem value="">
                <em>All</em>
              </MenuItem>
              {uniqueVendors.map((vendor) => (
                <MenuItem key={vendor} value={vendor}>
                  {vendor}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <Box sx={{ mb: 2 }}>
            <Typography gutterBottom>
              Cost Range (${costRange[0]} - ${costRange[1]})
            </Typography>
            <Slider
              value={costRange}
              onChange={(e, newValue) => setCostRange(newValue)}
              valueLabelDisplay="auto"
              min={0}
              max={1000}
            />
          </Box>
          <Button
            variant="contained"
            color="primary"
            onClick={applyFilters}
            fullWidth
            sx={{ mb: 1 }}
          >
            Apply Filters
          </Button>
          <Button variant="outlined" onClick={resetFilters} fullWidth>
            Reset Filters
          </Button>
        </Box>
      </Drawer>

      {/* Main Content: DataGrid Table */}
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Internal Product List
        </Typography>
        <div style={{ height: 600, width: "100%" }}>
          <DataGrid
            rows={filteredProducts}
            columns={columns}
            pageSize={10}
            rowsPerPageOptions={[10, 25, 50]}
            disableSelectionOnClick
          />
        </div>
      </Box>
    </Box>
  );
};

export default InternalProductList;
