import React, { useEffect, useState } from "react";
import { DataGrid } from "@mui/x-data-grid";
import {
  Box,
  Drawer,
  Typography,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stack
} from "@mui/material";
import ProductFormDialog from "./ProductFormDialog";
import CustomizeColumnsDialog from "./CustomizeColumnsDialog";
import { fetchProducts, updateProduct, createProduct } from "../api";

const drawerWidth = 300;

// Base columns (with Title moved first)
const baseColumns = [
  { field: "title", headerName: "Title", width: 200 },
  { field: "amazon_url", headerName: "Amazon URL", width: 200 },
  { field: "asin", headerName: "ASIN", width: 120 },
  { field: "price", headerName: "Price", width: 120, type: "number" },
  { field: "moq", headerName: "MOQ", width: 100, type: "number" },
  { field: "qty", headerName: "Quantity", width: 100, type: "number" },
  { field: "upc", headerName: "UPC", width: 120 },
  { field: "cost", headerName: "Cost", width: 120, type: "number" },
  { field: "vendor", headerName: "Vendor", width: 150 },
  { field: "lead_time", headerName: "Lead Time", width: 150 },
  { field: "exp_date", headerName: "Exp Date", width: 150 },
  { field: "fob", headerName: "FOB", width: 150 },
  { field: "profit_moq", headerName: "Profit MOQ", width: 150, type: "number" },
  { field: "image_url", headerName: "Image URL", width: 200 },
  { field: "category", headerName: "Category", width: 150 },
  { field: "out_of_stock", headerName: "Out of Stock", width: 150, type: "boolean" },
  { field: "walmart_url", headerName: "Walmart URL", width: 200 },
  { field: "ebay_url", headerName: "eBay URL", width: 200 }
];

// Actions column (Edit button)
const actionsColumn = {
  field: "actions",
  headerName: "Actions",
  width: 150,
  sortable: false,
  renderCell: (params) => (
    <Button
      variant="contained"
      color="primary"
      size="small"
      onClick={() => params.row?.handleEdit?.()}
    >
      Edit
    </Button>
  )
};

const InternalProductList = ({ onBack }) => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedIds, setSelectedIds] = useState([]);

  // Dialog state
  const [dialogOpen, setDialogOpen] = useState(false);
  const [dialogMode, setDialogMode] = useState("edit");
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Filter state
  const [categoryFilter, setCategoryFilter] = useState("");
  const [vendorFilter, setVendorFilter] = useState("");
  const [stockFilter, setStockFilter] = useState("in");

  useEffect(() => {
    const loadProducts = async () => {
      try {
        const data = await fetchProducts();
        const productData = data.products || data;

        const enriched = productData.map((p, index) => ({
          ...p,
          id: p.id ?? p._id ?? (index + 1),
          handleEdit: () => handleEditClick(p)
        }));

        setProducts(enriched);
        setFilteredProducts(enriched.filter(p => !p.out_of_stock));
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    loadProducts();
  }, []);

  const handleEditClick = (product) => {
    setSelectedProduct(product);
    setDialogMode("edit");
    setDialogOpen(true);
  };

  const handleAddClick = () => {
    setSelectedProduct(null);
    setDialogMode("add");
    setDialogOpen(true);
  };

  const handleFormSubmit = async (productData) => {
    if (dialogMode === "edit") {
      await updateProduct(selectedProduct.id || selectedProduct._id, productData);
    } else {
      await createProduct(productData);
    }
    setDialogOpen(false);
    setProducts([]);
  };

  const handleSelectionChange = (newSelection) => {
    const normalized = newSelection.map((id) => String(id));
    console.log("Selected IDs:", normalized);
    setSelectedIds(normalized);
  };

  const handleSendIndividualEmail = () => {
    console.log("Selected IDs:", selectedIds);
    const selectedProducts = filteredProducts.filter(p => {
      const productId = String(p.id || p._id);
      return productId && selectedIds.includes(productId);
    });

    if (selectedProducts.length === 0) {
      alert("No valid products selected for individual email.");
      return;
    }

    alert(`Send Individual Emails function triggered for ${selectedProducts.length} products.`);
  };

  const handleSendGroupEmail = () => {
    console.log("Selected IDs:", selectedIds);
    const selectedProducts = filteredProducts.filter(p => {
      const productId = String(p.id || p._id);
      return productId && selectedIds.includes(productId);
    });

    if (selectedProducts.length === 0) {
      alert("No valid products selected for group email.");
      return;
    }

    alert(`Send Group Email function triggered for ${selectedProducts.length} products.`);
  };

  return (
    <Box sx={{ display: "flex" }}>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: "border-box" }
        }}
      >
        <Box sx={{ p: 2 }}>
          {onBack && (
            <Button variant="contained" onClick={onBack} sx={{ mb: 2 }}>
              Back to Public View
            </Button>
          )}
          <Typography variant="h6" gutterBottom>Filters</Typography>
          <FormControl fullWidth sx={{ mb: 2 }}>
            <InputLabel id="category-select-label">Category</InputLabel>
            <Select
              labelId="category-select-label"
              value={categoryFilter}
              onChange={(e) => setCategoryFilter(e.target.value)}
            >
              <MenuItem value=""><em>All</em></MenuItem>
            </Select>
          </FormControl>
          <Stack spacing={1}>
            <Button variant="contained" color="success" onClick={handleSendIndividualEmail}>
              Send Individual Emails
            </Button>
            <Button variant="contained" color="success" onClick={handleSendGroupEmail}>
              Send Group Email
            </Button>
          </Stack>
        </Box>
      </Drawer>

      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h4" gutterBottom>Internal Product List</Typography>
        <div style={{ height: 600, width: "100%" }}>
          <DataGrid
            rows={filteredProducts}
            columns={[...baseColumns, actionsColumn]}
            getRowId={(row) => String(row.id)}
            pageSize={10}
            checkboxSelection
            selectionModel={selectedIds}
            onSelectionModelChange={handleSelectionChange}
          />
        </div>
      </Box>
    </Box>
  );
};

export default InternalProductList;
