 * - POST /products/{id}/send-email => body: { content: "...fancy HTML..." }
 * - EXACT logic you had for "individual" that was formatting well
 */
export async function sendIndividualEmails(selectedProducts) {
  if (!selectedProducts || !selectedProducts.length) {
    console.error("No products selected for individual email.");
    return;
  }

  for (const product of selectedProducts) {
    if (!product?.id) {
      console.warn("Skipping product with no ID:", product);
      continue;
