export async function sendGroupEmail(selectedProducts) {
  if (!selectedProducts || !selectedProducts.length) {
    console.error("No products selected for group email.");
    return;
  }

  // Merged fancy HTML for group
  const mergedHTML = selectedProducts.map((p) => buildEmailBody(p)).join("<hr>");

  // numeric IDs
  const productIds = selectedProducts.map((p) => p.id);

  console.log(`üîπ Sending GROUP email for product IDs: ${productIds.join(", ")}`);

  try {
