      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        product_ids: productIds,
        content: mergedHTML,
      }),
    });

    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(`HTTP ${response.status} - ${errorText}`);
