import React, { useEffect, useState } from "react";
import { DataGrid } from "@mui/x-data-grid";
import {
  Box,
  Drawer,
  Typography,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stack,
} from "@mui/material";
import ProductFormDialog from "./ProductFormDialog";
import CustomizeColumnsDialog from "./CustomizeColumnsDialog";
import { fetchProducts, createProduct, updateProduct } from "../api";
// ✅ Import the final "emailSender.js" with formatting
import { sendIndividualEmails, sendGroupEmail } from "../emailSender";

const drawerWidth = 300;

// Base columns (unchanged from your older snippet)
const baseColumns = [
  { field: "title", headerName: "Title", width: 200 },
  { field: "category", headerName: "Category", width: 200 },
  { field: "vendor", headerName: "Vendor", width: 150 },
  { field: "price", headerName: "Price", width: 100, type: "number" },
  { field: "cost", headerName: "Cost", width: 100, type: "number" },
  { field: "moq", headerName: "MOQ", width: 100, type: "number" },
  { field: "qty", headerName: "Qty", width: 100, type: "number" },
  { field: "upc", headerName: "UPC", width: 120 },
  { field: "asin", headerName: "ASIN", width: 120 },
  { field: "lead_time", headerName: "Lead Time", width: 120 },
  { field: "exp_date", headerName: "Exp Date", width: 120 },
  { field: "fob", headerName: "FOB", width: 100 },
  { field: "vendor_id", headerName: "Vendor ID", width: 120 },
  { field: "profit_moq", headerName: "Profit MOQ", width: 120, type: "number" },
  { field: "image_url", headerName: "Image URL", width: 200 },
  { field: "out_of_stock", headerName: "Out of Stock", width: 110, type: "boolean" },
  { field: "amazon_url", headerName: "Amazon URL", width: 200 },
  { field: "walmart_url", headerName: "Walmart URL", width: 200 },
  { field: "ebay_url", headerName: "eBay URL", width: 200 },
  // If you have timestamps, add them here
  { field: "offer_date", headerName: "Offer Date", width: 180 },
  { field: "last_sent", headerName: "Last Sent", width: 180 },
  {
    field: "actions",
    headerName: "Actions",
    width: 130,
    sortable: false,
    renderCell: (params) => {
      const handleClick = (e) => {
        e.stopPropagation();
        if (params.api && params.api.handleEditClick) {
          params.api.handleEditClick(params.row);
        }
      };
      return (
        <Button variant="outlined" size="small" onClick={handleClick}>
          Edit
        </Button>
      );
    },
  },
];

const allCategories = [
  // your categories...
];
const allVendors = [
  // your vendors...
];

const InternalProductList = ({ onBack }) => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedIds, setSelectedIds] = useState([]);

  // Filter states
  const [categoryFilter, setCategoryFilter] = useState("");
  const [vendorFilter, setVendorFilter] = useState("");

  // Column visibility
  const [columnVisibilityModel, setColumnVisibilityModel] = useState({});

  // Dialog states
  const [dialogOpen, setDialogOpen] = useState(false);
  const [dialogMode, setDialogMode] = useState("add");
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Customize columns
  const [customizeDialogOpen, setCustomizeDialogOpen] = useState(false);

  // Pagination
  const [pageSize, setPageSize] = useState(10);

  // Load column vis model
  useEffect(() => {
    const savedModel = localStorage.getItem("columnVisibilityModel");
    if (savedModel) {
      setColumnVisibilityModel(JSON.parse(savedModel));
    } else {
      const defaultModel = {};
      baseColumns.forEach((col) => {
        defaultModel[col.field] = true;
      });
      setColumnVisibilityModel(defaultModel);
    }
  }, []);

  // Fetch products once on mount
  useEffect(() => {
    loadProducts();
  }, []);

  async function loadProducts() {
    try {
      const data = await fetchProducts();
      const productData = data.products || data;
      setProducts(productData);
      setFilteredProducts(productData);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  }

  const handleColumnVisibilityChange = (newModel) => {
    setColumnVisibilityModel(newModel);
    localStorage.setItem("columnVisibilityModel", JSON.stringify(newModel));
  };

  // Filtering
  const applyFilters = () => {
    let temp = [...products];
    if (categoryFilter) {
      temp = temp.filter((p) => (p.category || "").toLowerCase() === categoryFilter.toLowerCase());
    }
    if (vendorFilter) {
      temp = temp.filter((p) => (p.vendor || "").toLowerCase() === vendorFilter.toLowerCase());
    }
    setFilteredProducts(temp);
  };

  const resetFilters = () => {
    setCategoryFilter("");
    setVendorFilter("");
    setFilteredProducts(products);
  };

  // Handle selection in DataGrid
  const handleSelectionModelChange = (newSelection) => {
    console.log("Selected IDs:", newSelection);
    setSelectedIds(newSelection);
  };

  // 1) Send multiple emails individually
  async function handleSendIndividualEmails() {
    // Gather the actual product objects from filteredProducts
    const selectedProducts = filteredProducts.filter((p) =>
      selectedIds.includes(String(p.id))
    );
    if (selectedProducts.length === 0) {
      alert("No products selected for individual email.");
      return;
    }
    console.log("Sending INDIVIDUAL emails for:", selectedProducts.map((p) => p.id));
    await sendIndividualEmails(selectedProducts);
    // Optionally reload after done to see updated last_sent
    loadProducts();
  }

  // 2) Send one group email
  async function handleSendGroupEmail() {
    const selectedProducts = filteredProducts.filter((p) =>
      selectedIds.includes(String(p.id))
    );
    if (selectedProducts.length === 0) {
      alert("No products selected for group email.");
      return;
    }
    console.log("Sending GROUP email for:", selectedProducts.map((p) => p.id));
    await sendGroupEmail(selectedProducts);
    loadProducts();
  }

  // Add / Edit
  const handleAddClick = () => {
    setDialogMode("add");
    setSelectedProduct(null);
    setDialogOpen(true);
  };

  const handleEditClick = (product) => {
    setDialogMode("edit");
    setSelectedProduct(product);
    setDialogOpen(true);
  };

  const handleFormSubmit = async (formData) => {
    if (dialogMode === "add") {
      await createProduct(formData);
    } else {
      if (!selectedProduct?.id) {
        console.error("No product ID to update");
        return;
      }
      await updateProduct(selectedProduct.id, formData);
    }
    setDialogOpen(false);
    loadProducts();
  };

  // Overwrite actions column to attach handleEditClick
  const columns = baseColumns.map((col) => {
    if (col.field === "actions") {
      return {
        ...col,
        renderCell: (params) => {
          const handleClick = (e) => {
            e.stopPropagation();
            handleEditClick(params.row);
          };
          return (
            <Button variant="outlined" size="small" onClick={handleClick}>
              Edit
            </Button>
          );
        },
      };
    }
    return col;
  });

  return (
    <Box sx={{ display: "flex" }}>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: {
            width: drawerWidth,
            boxSizing: "border-box",
          },
        }}
      >
        <Box sx={{ p: 2 }}>
          {onBack && (
            <Button variant="contained" onClick={onBack} sx={{ mb: 2 }}>
              Back to Public View
            </Button>
          )}
          <Typography variant="h6" gutterBottom>
            Filters & Actions
          </Typography>

          <Stack spacing={1} sx={{ mb: 2 }}>
            {/* Category */}
            <FormControl size="small" fullWidth>
              <InputLabel>Category</InputLabel>
              <Select
                value={categoryFilter}
                label="Category"
                onChange={(e) => setCategoryFilter(e.target.value)}
              >
                <MenuItem value="">(All)</MenuItem>
                {allCategories.map((cat) => (
                  <MenuItem key={cat} value={cat}>
                    {cat}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            {/* Vendor */}
            <FormControl size="small" fullWidth>
              <InputLabel>Vendor</InputLabel>
              <Select
                value={vendorFilter}
                label="Vendor"
                onChange={(e) => setVendorFilter(e.target.value)}
              >
                <MenuItem value="">(All)</MenuItem>
                {allVendors.map((v) => (
                  <MenuItem key={v} value={v}>
                    {v}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            <Button variant="contained" onClick={applyFilters}>
              Apply Filters
            </Button>
            <Button variant="outlined" onClick={resetFilters}>
              Reset Filters
            </Button>
          </Stack>

          <Stack spacing={1}>
            <Button variant="contained" onClick={handleAddClick}>
              Add Product
            </Button>
            <Button
              variant="contained"
              color="info"
              onClick={() => setCustomizeDialogOpen(true)}
            >
              Customize Columns
            </Button>

            {/* Our final email sending approach */}
            <Button variant="contained" color="success" onClick={handleSendIndividualEmails}>
              Send Individual Emails
            </Button>
            <Button variant="contained" color="success" onClick={handleSendGroupEmail}>
              Send Group Email
            </Button>
          </Stack>
        </Box>
      </Drawer>

      {/* Main Content */}
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Internal Product List
        </Typography>
        <div style={{ height: 600, width: "100%" }}>
          <DataGrid
            rows={filteredProducts}
            columns={columns}
            getRowId={(row) => String(row.id)}
            checkboxSelection
            selectionModel={selectedIds}
            onSelectionModelChange={handleSelectionModelChange}
            pagination
            pageSize={pageSize}
            onPageSizeChange={setPageSize}
            rowsPerPageOptions={[5, 10, 25, 50]}
            columnVisibilityModel={columnVisibilityModel}
          />
        </div>
      </Box>

      <ProductFormDialog
        open={dialogOpen}
        mode={dialogMode}
        initialData={selectedProduct}
        onClose={() => setDialogOpen(false)}
        onSubmit={handleFormSubmit}
      />

      <CustomizeColumnsDialog
        open={customizeDialogOpen}
        onClose={() => setCustomizeDialogOpen(false)}
        columns={baseColumns}
        columnVisibilityModel={columnVisibilityModel}
        onColumnVisibilityChange={handleColumnVisibilityChange}
      />
    </Box>
  );
};

export default InternalProductList;
