import React, { useEffect, useState } from "react";
import { DataGrid } from "@mui/x-data-grid";
import {
  Box,
  Drawer,
  Typography,
  Button,
  Slider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stack
} from "@mui/material";
import ProductFormDialog from "./ProductFormDialog";
import CustomizeColumnsDialog from "./CustomizeColumnsDialog";
import { fetchProducts, updateProduct, createProduct } from "../api";

const drawerWidth = 300;

// Base columns for product fields (editable by the user)
const baseColumns = [
  { field: "title", headerName: "Title", width: 200 }, // moved to first column
  { field: "amazon_url", headerName: "Amazon URL", width: 200 },
  { field: "asin", headerName: "ASIN", width: 120 },
  { field: "price", headerName: "Price", width: 120, type: "number" },
  { field: "moq", headerName: "MOQ", width: 100, type: "number" },
  { field: "qty", headerName: "Quantity", width: 100, type: "number" },
  { field: "upc", headerName: "UPC", width: 120 },
  { field: "cost", headerName: "Cost", width: 120, type: "number" },
  { field: "vendor", headerName: "Vendor", width: 150 },
  { field: "lead_time", headerName: "Lead Time", width: 150 },
  { field: "exp_date", headerName: "Exp Date", width: 150 },
  { field: "fob", headerName: "FOB", width: 150 },
  { field: "vendor_id", headerName: "Vendor ID", width: 150 },
  { field: "profit_moq", headerName: "Profit MOQ", width: 150, type: "number" },
  { field: "image_url", headerName: "Image URL", width: 200 },
  { field: "category", headerName: "Category", width: 150 },
  { field: "out_of_stock", headerName: "Out of Stock", width: 150, type: "boolean" },
  { field: "walmart_url", headerName: "Walmart URL", width: 200 },
  { field: "ebay_url", headerName: "eBay URL", width: 200 },
  { field: "estimated_sales_per_month", headerName: "Est. Sales/Month", width: 150, type: "number" },
  { field: "estimated_profit_per_unit", headerName: "Est. Profit/Unit", width: 150, type: "number" }
];

// Computed column: Estimated Time to Sell MOQ = moq / estimated_sales_per_month
const computedColumn = {
  field: "estimated_time_to_sell_moq",
  headerName: "Est. Time to Sell MOQ",
  width: 180,
  valueGetter: (params) => {
    if (!params || !params.row) return "";
    const moq = params.row.moq;
    const sales = params.row.estimated_sales_per_month;
    if (!sales || sales <= 0) return "N/A";
    return (moq / sales).toFixed(1) + " months";
  }
};

// Action column (cannot be hidden)
const actionsColumn = {
  field: "actions",
  headerName: "Actions",
  width: 150,
  sortable: false,
  renderCell: (params) => (
    <Button
      variant="contained"
      color="primary"
      size="small"
      onClick={() => params.api.getRow(params.id) && params.row.handleEdit && params.row.handleEdit(params.row)}
    >
      Edit
    </Button>
  )
};

const InternalProductList = ({ onBack }) => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  // Filter state
  const [categoryFilter, setCategoryFilter] = useState("");
  const [vendorFilter, setVendorFilter] = useState("");
  const [costRange, setCostRange] = useState([0, 1000000]);
  const [stockFilter, setStockFilter] = useState("in"); // "in" for In Stock (default), "out", "all"

  // State for controlling the product form dialog (for editing/adding)
  const [dialogOpen, setDialogOpen] = useState(false);
  const [dialogMode, setDialogMode] = useState("edit"); // "edit" or "add"
  const [selectedProduct, setSelectedProduct] = useState(null);

  // State for row selection in DataGrid
  const [selectedIds, setSelectedIds] = useState([]);

  // State for column customization
  const [columnVisibilityModel, setColumnVisibilityModel] = useState({});
  const [customizeDialogOpen, setCustomizeDialogOpen] = useState(false);

  // On mount, load column preferences from localStorage
  useEffect(() => {
    const savedModel = localStorage.getItem("columnVisibilityModel");
    if (savedModel) {
      setColumnVisibilityModel(JSON.parse(savedModel));
    } else {
      // Default: all base columns visible (actions column always visible)
      const defaultModel = {};
      baseColumns.forEach((col) => {
        defaultModel[col.field] = true;
      });
      // Also set computed column visible by default
      defaultModel[computedColumn.field] = true;
      setColumnVisibilityModel(defaultModel);
    }
  }, []);

  // Save column preferences to localStorage when changed
  const handleColumnVisibilityChange = (newModel) => {
    setColumnVisibilityModel(newModel);
    localStorage.setItem("columnVisibilityModel", JSON.stringify(newModel));
  };

  // Fetch products from backend
  const loadProducts = async () => {
    try {
      const data = await fetchProducts();
      const productData = data.products || data;
      // Add a handleEdit function to each row for convenience
      const enriched = productData.map((p) => ({ ...p, handleEdit: handleEditClick }));
      setProducts(enriched);
      setFilteredProducts(enriched);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  useEffect(() => {
    loadProducts();
  }, []);

  // Filtering functions (including stock filter)
  const applyFilters = () => {
    let filtered = products;
    if (categoryFilter) {
      filtered = filtered.filter((p) => p.category === categoryFilter);
    }
    if (vendorFilter) {
      filtered = filtered.filter((p) => p.vendor === vendorFilter);
    }
    if (costRange && costRange.length === 2) {
      filtered = filtered.filter((p) => p.cost >= costRange[0] && p.cost <= costRange[1]);
    }
    if (stockFilter === "in") {
      filtered = filtered.filter((p) => !p.out_of_stock);
    } else if (stockFilter === "out") {
      filtered = filtered.filter((p) => p.out_of_stock);
    }
    setFilteredProducts(filtered);
  };

  const resetFilters = () => {
    setCategoryFilter("");
    setVendorFilter("");
    setCostRange([0, 1000000]);
    setStockFilter("in");
    setFilteredProducts(products.filter((p) => !p.out_of_stock));
  };

  // Handlers for dialog open (edit or add)
  const handleEditClick = (product) => {
    setSelectedProduct(product);
    setDialogMode("edit");
    setDialogOpen(true);
  };

  const handleAddClick = () => {
    setSelectedProduct(null);
    setDialogMode("add");
    setDialogOpen(true);
  };

  const handleFormSubmit = async (productData) => {
    if (dialogMode === "edit") {
      await updateProduct(selectedProduct.id, productData);
    } else {
      await createProduct(productData);
    }
    setDialogOpen(false);
    loadProducts();
  };

  // Handle row selection change
  const handleSelectionChange = (newSelection) => {
    setSelectedIds(newSelection);
  };

  // Handlers for sending emails
  const handleSendIndividualEmail = () => {
    const selectedProducts = filteredProducts.filter((p) =>
      selectedIds.includes(p.id)
    );
    console.log("Sending individual emails for:", selectedProducts);
    alert("Send Individual Emails function triggered for " + selectedProducts.length + " products.");
    // Integrate your email sender function here
  };

  const handleSendGroupEmail = () => {
    const selectedProducts = filteredProducts.filter((p) =>
      selectedIds.includes(p.id)
    );
    console.log("Sending group email for:", selectedProducts);
    alert("Send Group Email function triggered for " + selectedProducts.length + " products.");
    // Integrate your email sender function here
  };

  // Combine all columns; allow DataGrid to use columnVisibilityModel and (if supported) reordering.
  const columns = [
    ...baseColumns,
    computedColumn,
    actionsColumn
  ];

  return (
    <Box sx={{ display: "flex" }}>
      {/* Sidebar Filters and Controls */}
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: "border-box" },
        }}
      >
        <Box sx={{ p: 2 }}>
          {onBack && (
            <Button variant="contained" onClick={onBack} sx={{ mb: 2 }}>
              Back to Public View
            </Button>
          )}
          <Typography variant="h6" gutterBottom>Filters</Typography>
          <FormControl fullWidth sx={{ mb: 2 }}>
            <InputLabel id="category-select-label">Category</InputLabel>
            <Select
              labelId="category-select-label"
              value={categoryFilter}
              label="Category"
              onChange={(e) => setCategoryFilter(e.target.value)}
            >
              <MenuItem value=""><em>All</em></MenuItem>
              {[...new Set(products.map(p => p.category).filter(Boolean))].map(cat => (
                <MenuItem key={cat} value={cat}>{cat}</MenuItem>
              ))}
            </Select>
          </FormControl>
          <FormControl fullWidth sx={{ mb: 2 }}>
            <InputLabel id="vendor-select-label">Vendor</InputLabel>
            <Select
              labelId="vendor-select-label"
              value={vendorFilter}
              label="Vendor"
              onChange={(e) => setVendorFilter(e.target.value)}
            >
              <MenuItem value=""><em>All</em></MenuItem>
              {[...new Set(products.map(p => p.vendor).filter(Boolean))].map(vendor => (
                <MenuItem key={vendor} value={vendor}>{vendor}</MenuItem>
              ))}
            </Select>
          </FormControl>
          <FormControl fullWidth sx={{ mb: 2 }}>
            <InputLabel id="stock-select-label">Stock Status</InputLabel>
            <Select
              labelId="stock-select-label"
              value={stockFilter}
              label="Stock Status"
              onChange={(e) => setStockFilter(e.target.value)}
            >
              <MenuItem value="in">In Stock</MenuItem>
              <MenuItem value="out">Out of Stock</MenuItem>
              <MenuItem value="all">All</MenuItem>
            </Select>
          </FormControl>
          <Box sx={{ mb: 2 }}>
            <Typography gutterBottom>Cost Range (${costRange[0]} - ${costRange[1]})</Typography>
            <Slider
              value={costRange}
              onChange={(e, newValue) => setCostRange(newValue)}
              valueLabelDisplay="auto"
              min={0}
              max={1000000}
            />
          </Box>
          <Stack spacing={1}>
            <Button variant="contained" color="primary" onClick={applyFilters}>
              Apply Filters
            </Button>
            <Button variant="outlined" onClick={resetFilters}>
              Reset Filters
            </Button>
            <Button variant="contained" color="secondary" onClick={handleAddClick}>
              Add Product
            </Button>
            <Button variant="contained" color="info" onClick={() => setCustomizeDialogOpen(true)}>
              Customize Columns
            </Button>
            <Button variant="contained" color="success" onClick={handleSendIndividualEmail}>
              Send Individual Emails
            </Button>
            <Button variant="contained" color="success" onClick={handleSendGroupEmail}>
              Send Group Email
            </Button>
          </Stack>
        </Box>
      </Drawer>

      {/* Main Content: DataGrid */}
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h4" gutterBottom>Internal Product List</Typography>
        <div style={{ height: 600, width: "100%" }}>
          <DataGrid
            rows={filteredProducts}
            columns={columns}
            pageSize={10}
            rowsPerPageOptions={[10, 25, 50]}
            checkboxSelection
            onSelectionModelChange={handleSelectionChange}
            columnVisibilityModel={columnVisibilityModel}
            onColumnVisibilityModelChange={handleColumnVisibilityChange}
            disableSelectionOnClick
            // Note: Column reordering is available in DataGridPro.
          />
        </div>
      </Box>

      {/* Product Form Dialog */}
      <ProductFormDialog
        open={dialogOpen}
        mode={dialogMode}
        initialData={selectedProduct}
        onClose={() => setDialogOpen(false)}
        onSubmit={handleFormSubmit}
      />

      {/* Customize Columns Dialog */}
      <CustomizeColumnsDialog
        open={customizeDialogOpen}
        onClose={() => setCustomizeDialogOpen(false)}
        columns={[...baseColumns, computedColumn]}  // allow toggling these columns
        initialVisibility={columnVisibilityModel}
        onSave={handleColumnVisibilityChange}
      />
    </Box>
  );
};

export default InternalProductList;
