import axios from "axios";
import { updateProduct } from "./api"; // ‚úÖ Ensures last_sent tracking works

const API_URL = "https://api.kit.com/v4/broadcasts";
const API_KEY = "kit_a0a183e8fd744ca557d96126e488ae22";

const headers = {
  Accept: "application/json",
  "Content-Type": "application/json",
  "X-Kit-Api-Key": API_KEY,
};

/**
 * ‚úÖ Send Individual Emails & Ensure last_sent is updated correctly
 */
export async function sendIndividualEmails(selectedProducts) {
  if (!selectedProducts || selectedProducts.length === 0) {
    console.error("No products selected for individual email.");
    return;
  }

  for (const product of selectedProducts) {
    if (!product?.id) {
      console.error("Skipping product with no ID:", product);
      continue;
    }

    const localUrl = `http://127.0.0.1:8000/products/${product.id}/send-email/`;
    console.log(`üîπ Sending individual email via FastAPI: POST ${localUrl}`);

    try {
      const emailData = {
        subject: product.title,
        content: buildPythonStyleEmail(product),  // ‚úÖ FIXED: Function is now defined!
        public: false,
        email_template_id: null
      };      
      
      console.log("\nüîç DEBUG: Payload being sent to Kit API:");
      console.log(JSON.stringify(emailData, null, 2)); // ‚úÖ Pretty-print JSON
      
      const response = await fetch(localUrl, {
        method: "POST",
        headers: {
          "Accept": "application/json",  // ‚úÖ FORCE HEADER CORRECTION
          "Content-Type": "application/json",
        },
        body: JSON.stringify(emailData),  // ‚úÖ Send emailData in request body
      });
      
      if (!response.ok) {
        const errText = await response.text();
        console.error(`‚ùå Failed to send single email for ID ${product.id}:`, errText);
      } else {
        const data = await response.json();
        console.log(`‚úÖ Email & last_sent updated for ID ${product.id}:`, data);
      }
    } catch (error) {
      console.error("‚ùå Network error sending single email:", error);
    }
  }
}

/**
 * ‚úÖ Send Group Email & Ensure last_sent is updated correctly
 */
export async function sendGroupEmail(selectedProducts) {
  if (!selectedProducts || selectedProducts.length === 0) {
    console.error("No products selected for group email.");
    return;
  }

  const productIds = selectedProducts.map((p) => p.id).filter(Boolean);
  if (!productIds.length) {
    console.error("Skipping group email, invalid product IDs.");
    return;
  }

  const localUrl = "http://127.0.0.1:8000/products/send-group-email/";
  console.log(`üîπ Sending group email via FastAPI: POST ${localUrl}`, productIds);

  const emailData = { product_ids: productIds };

  try {
    console.log("\nüîç DEBUG: Payload being sent to Kit API:");
    console.log(JSON.stringify(emailData, null, 2)); // Pretty-print JSON

    const response = await fetch(localUrl, {
      method: "POST",
      headers: {
        "Accept": "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(emailData),
    });
    
    if (!response.ok) {
      const errText = await response.text();
      console.error("‚ùå Failed to send group email:", errText);
    } else {
      const data = await response.json();
      console.log(`‚úÖ Group email & last_sent updated for IDs ${productIds}:`, data);
    }
  } catch (error) {
    console.error("‚ùå Network error sending group email:", error);
  }
}
/**
 * ‚úÖ Build the HTML Email EXACTLY Like Python Code
 */
function buildPythonStyleEmail(product) {
  const title = (product.title || "").trim();
  const imageUrl = product.image_url || "";
  const amazonUrl = product.amazon_url || "";
  const walmartUrl = product.walmart_url || "";
  const ebayUrl = product.ebay_url || "";
  const price = product.price ? parseFloat(product.price).toFixed(2) : "0.00";
  const moq = product.moq || "0";
  const qty = product.qty || "0";
  const asin = product.asin || "";
  const fob = product.fob || "";
  const expDate = product.exp_date || "";
  let leadTime = product.lead_time || "";

  if (leadTime.includes("Days")) {
    leadTime = leadTime.replace("Days", "Business Days");
  }

  let emailBody = `
    <div style="padding: 20px 0;">
      <div style="text-align:center; margin-bottom: 60px;">
        <h2 style="font-size:18px; margin-bottom: 20px;">${title}</h2>
        <img src="${imageUrl}" alt="${title}"
             style="max-width:300px; max-height:300px; width:auto; height:auto; object-fit:contain;
                    display:block; margin-left:auto; margin-right:auto; margin-top:40px; margin-bottom:40px;"/>
      </div>
  `;

  if (amazonUrl.includes("https")) {
    emailBody += `<a href="${amazonUrl}"
       style="background-color:#FF9900; color:white; padding:15px 25px; text-align:center;
              text-decoration:none; display:inline-block; font-size:16px; margin:5px; border-radius:5px;">
       Amazon Link
    </a>`;
  }
  if (walmartUrl.includes("https")) {
    emailBody += `<a href="${walmartUrl}"
       style="background-color:#0071CE; color:white; padding:15px 25px; text-align:center;
              text-decoration:none; display:inline-block; font-size:16px; margin:5px; border-radius:5px;">
       Walmart Link
    </a>`;
  }
  if (ebayUrl.includes("https")) {
    emailBody += `<a href="${ebayUrl}"
       style="background-color:#E53238; color:white; padding:15px 25px; text-align:center;
              text-decoration:none; display:inline-block; font-size:16px; margin:5px; border-radius:5px;">
       eBay Link
    </a>`;
  }

  emailBody += `
      <p style="font-size:18px; margin-top:40px; margin-bottom: 10px;">
        $${price} EA, MOQ ${moq}, ${qty} Available.
      </p>`;

  if (asin) emailBody += `<p style="font-size:18px; margin-bottom: 10px;">ASIN: ${asin}</p>`;
  if (fob) emailBody += `<p style="font-size:18px; margin-bottom: 10px;">FOB: ${fob}</p>`;
  if (expDate) emailBody += `<p style="font-size:18px; margin-bottom: 10px;">Expiration Date: ${expDate}</p>`;
  emailBody += `<p style="font-size:18px; margin-bottom: 20px;">Lead Time: ${leadTime}</p></div></div>`;

  return emailBody;
}
