import React, { useEffect, useState } from "react";
import { DataGrid } from "@mui/x-data-grid";
import {
  Box,
  Drawer,
  Typography,
  Button,
  Slider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stack
} from "@mui/material";
import ProductFormDialog from "./ProductFormDialog";
import CustomizeColumnsDialog from "./CustomizeColumnsDialog";
import { fetchProducts, updateProduct, createProduct } from "../api";

const drawerWidth = 300;

// Base columns configuration
const baseColumns = [
  { field: "title", headerName: "Title", width: 200 },
  { field: "amazon_url", headerName: "Amazon URL", width: 200 },
  { field: "asin", headerName: "ASIN", width: 120 },
  { field: "price", headerName: "Price", width: 120, type: "number" },
  { field: "moq", headerName: "MOQ", width: 100, type: "number" },
  { field: "qty", headerName: "Quantity", width: 100, type: "number" },
  { field: "upc", headerName: "UPC", width: 120 },
  { field: "cost", headerName: "Cost", width: 120, type: "number" },
  { field: "vendor", headerName: "Vendor", width: 150 },
  { field: "lead_time", headerName: "Lead Time", width: 150 },
  { field: "exp_date", headerName: "Exp Date", width: 150 },
  { field: "fob", headerName: "FOB", width: 150 },
  { field: "vendor_id", headerName: "Vendor ID", width: 150 },
  { field: "profit_moq", headerName: "Profit MOQ", width: 150, type: "number" },
  { field: "image_url", headerName: "Image URL", width: 200 },
  { field: "category", headerName: "Category", width: 150 },
  { field: "out_of_stock", headerName: "Out of Stock", width: 150, type: "boolean" },
  { field: "walmart_url", headerName: "Walmart URL", width: 200 },
  { field: "ebay_url", headerName: "eBay URL", width: 200 },
  { field: "estimated_sales_per_month", headerName: "Est. Sales/Month", width: 150, type: "number" },
  { field: "estimated_profit_per_unit", headerName: "Est. Profit/Unit", width: 150, type: "number" }
];

const computedColumn = {
  field: "estimated_time_to_sell_moq",
  headerName: "Est. Time to Sell MOQ",
  width: 180,
  valueGetter: (params) => {
    if (!params?.row?.moq || !params?.row?.estimated_sales_per_month) return "N/A";
    const result = params.row.moq / params.row.estimated_sales_per_month;
    return result > 0 ? `${result.toFixed(1)} months` : "N/A";
  }
};

const actionsColumn = {
  field: "actions",
  headerName: "Actions",
  width: 150,
  sortable: false,
  renderCell: ({ row }) => (
    <Button
      variant="contained"
      color="primary"
      size="small"
      onClick={() => row.handleEdit(row)}
    >
      Edit
    </Button>
  )
};

const InternalProductList = ({ onBack }) => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [categoryFilter, setCategoryFilter] = useState("");
  const [vendorFilter, setVendorFilter] = useState("");
  const [costRange, setCostRange] = useState([0, 1000000]);
  const [stockFilter, setStockFilter] = useState("in");
  const [dialogOpen, setDialogOpen] = useState(false);
  const [dialogMode, setDialogMode] = useState("edit");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedIds, setSelectedIds] = useState([]);
  const [columnVisibilityModel, setColumnVisibilityModel] = useState({});
  const [customizeDialogOpen, setCustomizeDialogOpen] = useState(false);

  useEffect(() => {
    const savedModel = localStorage.getItem("columnVisibilityModel");
    if (savedModel) {
      setColumnVisibilityModel(JSON.parse(savedModel));
    } else {
      const defaultModel = [...baseColumns, computedColumn].reduce((acc, col) => {
        acc[col.field] = true;
        return acc;
      }, {});
      setColumnVisibilityModel(defaultModel);
    }
  }, []);

  const loadProducts = async () => {
    try {
      const data = await fetchProducts();
      const productData = Array.isArray(data.products) ? data.products : data;
      const enriched = productData.map(p => ({
        ...p,
        _id: p._id?.toString() || p.id?.toString(),
        handleEdit: handleEditClick
      }));
      setProducts(enriched);
      setFilteredProducts(enriched.filter(p => !p.out_of_stock));
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  const applyFilters = () => {
    let filtered = products.filter(p => {
      const matchesCategory = !categoryFilter || p.category === categoryFilter;
      const matchesVendor = !vendorFilter || p.vendor === vendorFilter;
      const matchesStock = stockFilter === "all" ? true : 
        stockFilter === "in" ? !p.out_of_stock : p.out_of_stock;
      const matchesCost = p.cost >= costRange[0] && p.cost <= costRange[1];
      return matchesCategory && matchesVendor && matchesStock && matchesCost;
    });
    setFilteredProducts(filtered);
  };

  // Email handlers with proper ID comparison
  const handleSendIndividualEmail = () => {
    const selectedProducts = filteredProducts.filter(p => 
      selectedIds.includes(p._id?.toString())
    );
    alert(`Triggered individual emails for ${selectedProducts.length} products`);
  };

  const handleSendGroupEmail = () => {
    const selectedProducts = filteredProducts.filter(p => 
      selectedIds.includes(p._id?.toString())
    );
    alert(`Triggered group email for ${selectedProducts.length} products`);
  };

  return (
    <Box sx={{ display: "flex" }}>
      <Drawer variant="permanent" sx={{ width: drawerWidth }}>
        <Box sx={{ p: 2 }}>
          {onBack && (
            <Button variant="contained" onClick={onBack} sx={{ mb: 2 }}>
              Back to Public View
            </Button>
          )}
          <Typography variant="h6" gutterBottom>Filters</Typography>
          
          {/* Filter controls */}
          <FormControl fullWidth sx={{ mb: 2 }}>
            <InputLabel>Category</InputLabel>
            <Select
              value={categoryFilter}
              onChange={(e) => setCategoryFilter(e.target.value)}
            >
              <MenuItem value=""><em>All</em></MenuItem>
              {[...new Set(products.map(p => p.category).filter(Boolean))].map(cat => (
                <MenuItem key={cat} value={cat}>{cat}</MenuItem>
              ))}
            </Select>
          </FormControl>

          {/* Other filter controls... */}

          <Stack spacing={1}>
            <Button variant="contained" color="primary" onClick={applyFilters}>
              Apply Filters
            </Button>
            <Button variant="outlined" onClick={() => {
              setCategoryFilter("");
              setVendorFilter("");
              setCostRange([0, 1000000]);
              setStockFilter("in");
              setFilteredProducts(products.filter(p => !p.out_of_stock));
            }}>
              Reset Filters
            </Button>
            <Button variant="contained" color="secondary" onClick={() => {
              setSelectedProduct(null);
              setDialogMode("add");
              setDialogOpen(true);
            }}>
              Add Product
            </Button>
            <Button variant="contained" color="info" onClick={() => setCustomizeDialogOpen(true)}>
              Customize Columns
            </Button>
            <Button variant="contained" color="success" onClick={handleSendIndividualEmail}>
              Send Individual Emails
            </Button>
            <Button variant="contained" color="success" onClick={handleSendGroupEmail}>
              Send Group Email
            </Button>
          </Stack>
        </Box>
      </Drawer>

      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h4" gutterBottom>Internal Product List</Typography>
        <div style={{ height: 600, width: "100%" }}>
          <DataGrid
            rows={filteredProducts}
            columns={[...baseColumns, computedColumn, actionsColumn]}
            getRowId={(row) => row._id}
            pageSize={10}
            rowsPerPageOptions={[10, 25, 50]}
            checkboxSelection
            onSelectionModelChange={(ids) => setSelectedIds(ids.map(String))}
            columnVisibilityModel={columnVisibilityModel}
            onColumnVisibilityModelChange={model => {
              setColumnVisibilityModel(model);
              localStorage.setItem("columnVisibilityModel", JSON.stringify(model));
            }}
            disableSelectionOnClick
          />
        </div>
      </Box>

      <ProductFormDialog
        open={dialogOpen}
        mode={dialogMode}
        initialData={selectedProduct}
        onClose={() => setDialogOpen(false)}
        onSubmit={async (data) => {
          if (dialogMode === "edit") {
            await updateProduct(selectedProduct._id, data);
          } else {
            await createProduct(data);
          }
          setDialogOpen(false);
          loadProducts();
        }}
      />

      <CustomizeColumnsDialog
        open={customizeDialogOpen}
        onClose={() => setCustomizeDialogOpen(false)}
        columns={[...baseColumns, computedColumn]}
        initialVisibility={columnVisibilityModel}
        onSave={setColumnVisibilityModel}
      />
    </Box>
  );
};

export default InternalProductList;
