  <!-- Details Section -->
  <div style="text-align:center;">
    <p style="font-size:18px; margin-top:40px; margin-bottom:10px;">
      <strong>$${price} EA</strong>, MOQ ${moq}, ${qty} Available.
    </p>
    ${asin ? `<p style="font-size:18px; margin-bottom:10px;">ASIN: ${asin}</p>` : ""}
