import React, { useEffect, useState } from "react";
import { DataGrid } from "@mui/x-data-grid";
import {
  Box,
  Drawer,
  Typography,
  Button,
  Slider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stack
} from "@mui/material";
import ProductFormDialog from "./ProductFormDialog";
import CustomizeColumnsDialog from "./CustomizeColumnsDialog";
import { fetchProducts, updateProduct, createProduct } from "../api";

const drawerWidth = 300;

// Base columns for DataGrid (you can add more columns later as needed)
const baseColumns = [
  { field: "title", headerName: "Title", width: 200 },
  { field: "price", headerName: "Price", width: 120, type: "number" },
  { field: "qty", headerName: "Quantity", width: 100, type: "number" },
  { field: "id", headerName: "Product ID", width: 120 } // For debugging, show ID
];

const InternalProductList = ({ onBack }) => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  
  // Filter state (if filtering is used)
  const [categoryFilter, setCategoryFilter] = useState("");
  const [vendorFilter, setVendorFilter] = useState("");
  const [costRange, setCostRange] = useState([0, 1000000]);
  const [stockFilter, setStockFilter] = useState("in"); // Default: In Stock
  
  // Dialog state for add/edit
  const [dialogOpen, setDialogOpen] = useState(false);
  const [dialogMode, setDialogMode] = useState("edit");
  const [selectedProduct, setSelectedProduct] = useState(null);
  
  // Controlled selection state: store selectedIds as strings.
  const [selectedIds, setSelectedIds] = useState([]);
  
  // Column customization state
  const [columnVisibilityModel, setColumnVisibilityModel] = useState({});
  const [customizeDialogOpen, setCustomizeDialogOpen] = useState(false);
  
  // Load column preferences on mount
  useEffect(() => {
    const savedModel = localStorage.getItem("columnVisibilityModel");
    if (savedModel) {
      setColumnVisibilityModel(JSON.parse(savedModel));
    } else {
      const defaultModel = {};
      baseColumns.forEach(col => defaultModel[col.field] = true);
      setColumnVisibilityModel(defaultModel);
    }
  }, []);
  
  const handleColumnVisibilityChange = (newModel) => {
    setColumnVisibilityModel(newModel);
    localStorage.setItem("columnVisibilityModel", JSON.stringify(newModel));
  };
  
  // Define handleEditClick: open edit dialog for a product.
  const handleEditClick = (product) => {
    setSelectedProduct(product);
    setDialogMode("edit");
    setDialogOpen(true);
  };
  
  // Define handleAddClick: open add product dialog.
  const handleAddClick = () => {
    setSelectedProduct(null);
    setDialogMode("add");
    setDialogOpen(true);
  };
  
  // Define handleFormSubmit: update or create a product.
  const handleFormSubmit = async (productData) => {
    if (dialogMode === "edit") {
      await updateProduct(selectedProduct.id || selectedProduct._id, productData);
    } else {
      await createProduct(productData);
    }
    setDialogOpen(false);
    loadProducts();
  };
  
  // Fetch products from API and assign a stable string id (do not re-map IDs on filtering)
  const loadProducts = async () => {
    try {
      const data = await fetchProducts();
      const productData = data.products || data;
      const enriched = productData.map((p, index) => ({
        ...p,
        id: String(p.id ?? p._id ?? (index + 1))
      }));
      setProducts(enriched);
      setFilteredProducts(enriched.filter(p => !p.out_of_stock));
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };
  
  useEffect(() => {
    loadProducts();
  }, []);
  
  // Simple filtering functions (if used)
  const applyFilters = () => {
    let filtered = products;
    if (categoryFilter) {
      filtered = filtered.filter(p => p.category === categoryFilter);
    }
    if (vendorFilter) {
      filtered = filtered.filter(p => p.vendor === vendorFilter);
    }
    if (costRange && costRange.length === 2) {
      filtered = filtered.filter(p => p.cost >= costRange[0] && p.cost <= costRange[1]);
    }
    if (stockFilter === "in") {
      filtered = filtered.filter(p => !p.out_of_stock);
    } else if (stockFilter === "out") {
      filtered = filtered.filter(p => p.out_of_stock);
    }
    setFilteredProducts(filtered);
  };
  
  const resetFilters = () => {
    setCategoryFilter("");
    setVendorFilter("");
    setCostRange([0, 1000000]);
    setStockFilter("in");
    setFilteredProducts(products.filter(p => !p.out_of_stock));
  };
  
  // Controlled selection: update selectedIds when selection changes.
  const handleSelectionModelChange = (newSelection) => {
    console.log("DataGrid selection change:", newSelection);
    // newSelection is an array of IDs (as strings because getRowId returns a string)
    setSelectedIds(newSelection);
  };
  
  // Email handlers: filter selected products by comparing their id.
  const handleSendIndividualEmail = () => {
    console.log("Filtered products IDs:", filteredProducts.map(p => p.id));
    console.log("Selected IDs:", selectedIds);
    const selectedProducts = filteredProducts.filter(p =>
      p.id && selectedIds.includes(p.id)
    );
    if (selectedProducts.length === 0) {
      alert("No valid products selected for individual email.");
      return;
    }
    alert(`Send Individual Emails triggered for ${selectedProducts.length} products.`);
    // Insert integration with your email sender function here.
  };
  
  const handleSendGroupEmail = () => {
    console.log("Filtered products IDs:", filteredProducts.map(p => p.id));
    console.log("Selected IDs:", selectedIds);
    const selectedProducts = filteredProducts.filter(p =>
      p.id && selectedIds.includes(p.id)
    );
    if (selectedProducts.length === 0) {
      alert("No valid products selected for group email.");
      return;
    }
    alert(`Send Group Email triggered for ${selectedProducts.length} products.`);
    // Insert integration with your email sender function here.
  };
  
  const columns = [...baseColumns];
  
  return (
    <Box sx={{ display: "flex" }}>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: "border-box" }
        }}
      >
        <Box sx={{ p: 2 }}>
          {onBack && (
            <Button variant="contained" onClick={onBack} sx={{ mb: 2 }}>
              Back to Public View
            </Button>
          )}
          <Typography variant="h6" gutterBottom>Filters & Actions</Typography>
          <Stack spacing={1}>
            <Button variant="contained" color="primary" onClick={applyFilters}>
              Apply Filters
            </Button>
            <Button variant="outlined" onClick={resetFilters}>
              Reset Filters
            </Button>
            <Button variant="contained" color="secondary" onClick={handleAddClick}>
              Add Product
            </Button>
            <Button variant="contained" color="success" onClick={handleSendIndividualEmail}>
              Send Individual Emails
            </Button>
            <Button variant="contained" color="success" onClick={handleSendGroupEmail}>
              Send Group Email
            </Button>
          </Stack>
        </Box>
      </Drawer>
  
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h4" gutterBottom>Internal Product List</Typography>
        <div style={{ height: 600, width: "100%" }}>
          <DataGrid
            rows={filteredProducts}
            columns={columns}
            getRowId={(row) => String(row.id)}
            pageSize={10}
            rowsPerPageOptions={[10, 25, 50]}
            checkboxSelection
            selectionModel={selectedIds}
            onSelectionModelChange={handleSelectionModelChange}
            disableSelectionOnClick={false}
            columnVisibilityModel={columnVisibilityModel}
            onColumnVisibilityModelChange={handleColumnVisibilityChange}
          />
        </div>
      </Box>
  
      <ProductFormDialog
        open={dialogOpen}
        mode={dialogMode}
        initialData={selectedProduct}
        onClose={() => setDialogOpen(false)}
        onSubmit={handleFormSubmit}
      />
  
      <CustomizeColumnsDialog
        open={customizeDialogOpen}
        onClose={() => setCustomizeDialogOpen(false)}
        columns={baseColumns}
        initialVisibility={columnVisibilityModel}
        onSave={handleColumnVisibilityChange}
      />
    </Box>
  );
};

export default InternalProductList;
