    const localUrl = `http://127.0.0.1:8000/products/${product.id}/send-email/`;
    console.log(`üîπ Sending individual email via FastAPI: POST ${localUrl}`);

    try {
      const response = await fetch(localUrl, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
      });
      if (!response.ok) {
        const errText = await response.text();
        console.error(`‚ùå Failed to send single email for ID ${product.id}:`, errText);
      } else {
        const data = await response.json();
        console.log(`‚úÖ Email & last_sent updated for ID ${product.id}:`, data);
      }
    } catch (error) {
      console.error("‚ùå Network error sending single email:", error);
    }
  }
}

/**
 * ‚úÖ Send Group Email & Ensure last_sent is updated correctly
