  try {
    const response = await fetch(localUrl, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(productIds),
    });
    if (!response.ok) {
      const errText = await response.text();
      console.error("‚ùå Failed to send group email:", errText);
    } else {
      const data = await response.json();
      console.log(`‚úÖ Group email & last_sent updated for IDs ${productIds}:`, data);
    }
  } catch (error) {
    console.error("‚ùå Network error sending group email:", error);
