import React, { useEffect, useState } from "react";
import { DataGrid } from "@mui/x-data-grid";
import {
  Box,
  Drawer,
  Typography,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stack,
} from "@mui/material";
import ProductFormDialog from "./ProductFormDialog";
import CustomizeColumnsDialog from "./CustomizeColumnsDialog";
// ✅ We no longer import sendIndividualEmails, sendGroupEmail from emailSender.
// Instead, we call new functions in api.js that do “send & update last_sent” in one step:
import {
  fetchProducts,
  createProduct,
  updateProduct,
  sendProductEmail,
  sendGroupEmail, // new function
} from "../api";

const drawerWidth = 300;

// Columns, including your date fields
const baseColumns = [
  { field: "title", headerName: "Title", width: 200 },
  { field: "category", headerName: "Category", width: 200 },
  { field: "vendor", headerName: "Vendor", width: 150 },
  { field: "price", headerName: "Price", width: 100, type: "number" },
  { field: "cost", headerName: "Cost", width: 100, type: "number" },
  { field: "moq", headerName: "MOQ", width: 100, type: "number" },
  { field: "qty", headerName: "Qty", width: 100, type: "number" },
  { field: "upc", headerName: "UPC", width: 120 },
  { field: "asin", headerName: "ASIN", width: 120 },
  { field: "lead_time", headerName: "Lead Time", width: 120 },
  { field: "exp_date", headerName: "Exp Date", width: 120 },
  { field: "fob", headerName: "FOB", width: 100 },
  { field: "vendor_id", headerName: "Vendor ID", width: 120 },
  { field: "profit_moq", headerName: "Profit MOQ", width: 120, type: "number" },
  { field: "image_url", headerName: "Image URL", width: 200 },
  { field: "out_of_stock", headerName: "Out of Stock", width: 110, type: "boolean" },
  { field: "amazon_url", headerName: "Amazon URL", width: 200 },
  { field: "walmart_url", headerName: "Walmart URL", width: 200 },
  { field: "ebay_url", headerName: "eBay URL", width: 200 },
  // ✅ Timestamps
  { field: "offer_date", headerName: "Offer Date", width: 180 },
  { field: "last_sent", headerName: "Last Sent", width: 180 },
  // Actions
  {
    field: "actions",
    headerName: "Actions",
    width: 130,
    sortable: false,
    renderCell: (params) => {
      const handleClick = (e) => {
        e.stopPropagation();
        if (params.api && params.api.handleEditClick) {
          params.api.handleEditClick(params.row);
        }
      };
      return (
        <Button variant="outlined" size="small" onClick={handleClick}>
          Edit
        </Button>
      );
    },
  },
];

// Categories/Vendors for filtering
const allCategories = [
  "Appliances",
  "Automotive",
  "Baby",
  "Beauty & Personal Care",
  "Cell Phones & Accessories",
  "Clothing, Shoes & Jewelry",
  "Electronics",
  "Health & Household",
  "Home & Kitchen",
  "Industrial & Industrial",
  "Kitchen & Dining",
  "Musical Instruments",
  "Patio, Lawn & Garden",
  "Pet Supplies",
  "Sports & Outdoors",
  "Tools & Home Improvement",
  "Toys & Games",
  "Office Products",
  "Grocery & Gourmet Food",
  "Video Games",
  "Arts, Crafts & Sewing",
  "Camera & Photo",
];

const allVendors = [
  "BSD",
  "Bulk",
  "123",
  "DA",
  "Fred",
  "UPD",
  "Haut",
  "NY Cos",
  "DTG",
  "MS",
  "GPS",
  "Cos",
  "Cust",
  "BB",
  "JW",
  "JAMN",
  "FlecHub",
  "Royal",
];

const InternalProductList = ({ onBack }) => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedIds, setSelectedIds] = useState([]);

  // Filters
  const [categoryFilter, setCategoryFilter] = useState("");
  const [vendorFilter, setVendorFilter] = useState("");

  // Columns
  const [columnVisibilityModel, setColumnVisibilityModel] = useState({});

  // Dialog states
  const [dialogOpen, setDialogOpen] = useState(false);
  const [dialogMode, setDialogMode] = useState("add");
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Customize Columns
  const [customizeDialogOpen, setCustomizeDialogOpen] = useState(false);

  // Pagination
  const [pageSize, setPageSize] = useState(10);

  // Load column visibility
  useEffect(() => {
    const savedModel = localStorage.getItem("columnVisibilityModel");
    if (savedModel) {
      setColumnVisibilityModel(JSON.parse(savedModel));
    } else {
      const defaultModel = {};
      baseColumns.forEach((col) => {
        defaultModel[col.field] = true;
      });
      setColumnVisibilityModel(defaultModel);
    }
  }, []);

  // Fetch products
  useEffect(() => {
    loadProducts();
  }, []);

  async function loadProducts() {
    try {
      const data = await fetchProducts();
      const productData = data.products || data;
      setProducts(productData);
      setFilteredProducts(productData);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  }

  const handleColumnVisibilityChange = (newModel) => {
    setColumnVisibilityModel(newModel);
    localStorage.setItem("columnVisibilityModel", JSON.stringify(newModel));
  };

  // Filtering
  const applyFilters = () => {
    let temp = [...products];
    if (categoryFilter) {
      temp = temp.filter((p) => String(p.category).toLowerCase() === categoryFilter.toLowerCase());
    }
    if (vendorFilter) {
      temp = temp.filter((p) => String(p.vendor).toLowerCase() === vendorFilter.toLowerCase());
    }
    setFilteredProducts(temp);
  };

  const resetFilters = () => {
    setCategoryFilter("");
    setVendorFilter("");
    setFilteredProducts(products);
  };

  // Selection
  const handleSelectionModelChange = (newSelection) => {
    console.log("Selected IDs:", newSelection);
    setSelectedIds(newSelection);
  };

  // ✅ A simpler approach: single endpoints to send & update last_sent
  async function handleSendEmail() {
    if (selectedIds.length !== 1) {
      alert("Please select exactly one product to send.");
      return;
    }
    const productId = selectedIds[0];
    try {
      // This calls a new endpoint in api.js: sendProductEmail(productId)
      await sendProductEmail(productId);
      alert("Email sent. last_sent updated!");
      // Refresh data so we see new last_sent
      await loadProducts();
    } catch (err) {
      console.error("Failed to send single email:", err);
      alert("Error sending email. Check console for details.");
    }
  }

  async function handleSendGroupEmail() {
    if (selectedIds.length < 1) {
      alert("No products selected for group email.");
      return;
    }
    try {
      // This calls a new endpoint in api.js: sendGroupEmail(productIds)
      await sendGroupEmail(selectedIds);
      alert("Group email sent. last_sent updated for each!");
      // Refresh data so we see new last_sent
      await loadProducts();
    } catch (err) {
      console.error("Failed to send group email:", err);
      alert("Error sending group email. Check console for details.");
    }
  }

  // Add / Edit
  const handleAddClick = () => {
    setDialogMode("add");
    setSelectedProduct(null);
    setDialogOpen(true);
  };

  const handleEditClick = (product) => {
    setDialogMode("edit");
    setSelectedProduct(product);
    setDialogOpen(true);
  };

  const handleFormSubmit = async (formData) => {
    if (dialogMode === "add") {
      await createProduct(formData);
    } else {
      if (!selectedProduct?.id) {
        console.error("No product ID to update");
        return;
      }
      await updateProduct(selectedProduct.id, formData);
    }
    setDialogOpen(false);
    await loadProducts();
  };

  // Overwrite actions column
  const columns = baseColumns.map((col) => {
    if (col.field === "actions") {
      return {
        ...col,
        renderCell: (params) => {
          const handleClick = (e) => {
            e.stopPropagation();
            handleEditClick(params.row);
          };
          return (
            <Button variant="outlined" size="small" onClick={handleClick}>
              Edit
            </Button>
          );
        },
      };
    }
    return col;
  });

  return (
    <Box sx={{ display: "flex" }}>
      {/* LEFT DRAWER */}
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: {
            width: drawerWidth,
            boxSizing: "border-box",
          },
        }}
      >
        <Box sx={{ p: 2 }}>
          {onBack && (
            <Button variant="contained" onClick={onBack} sx={{ mb: 2 }}>
              Back to Public View
            </Button>
          )}
          <Typography variant="h6" gutterBottom>
            Filters & Actions
          </Typography>

          <Stack spacing={1} sx={{ mb: 2 }}>
            {/* Category Filter */}
            <FormControl size="small" fullWidth>
              <InputLabel>Category</InputLabel>
              <Select
                value={categoryFilter}
                label="Category"
                onChange={(e) => setCategoryFilter(e.target.value)}
              >
                <MenuItem value="">(All)</MenuItem>
                {allCategories.map((cat) => (
                  <MenuItem key={cat} value={cat}>
                    {cat}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            {/* Vendor Filter */}
            <FormControl size="small" fullWidth>
              <InputLabel>Vendor</InputLabel>
              <Select
                value={vendorFilter}
                label="Vendor"
                onChange={(e) => setVendorFilter(e.target.value)}
              >
                <MenuItem value="">(All)</MenuItem>
                {allVendors.map((v) => (
                  <MenuItem key={v} value={v}>
                    {v}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            <Button variant="contained" onClick={applyFilters}>
              Apply Filters
            </Button>
            <Button variant="outlined" onClick={resetFilters}>
              Reset Filters
            </Button>
          </Stack>

          {/* Actions */}
          <Stack spacing={1}>
            <Button variant="contained" onClick={handleAddClick}>
              Add Product
            </Button>
            <Button
              variant="contained"
              color="info"
              onClick={() => setCustomizeDialogOpen(true)}
            >
              Customize Columns
            </Button>

            {/* Our new “Send Email” approach */}
            <Button variant="contained" color="success" onClick={handleSendEmail}>
              Send Email (Single)
            </Button>
            <Button variant="contained" color="success" onClick={handleSendGroupEmail}>
              Send Group Email
            </Button>
          </Stack>
        </Box>
      </Drawer>

      {/* MAIN CONTENT */}
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Internal Product List
        </Typography>
        <div style={{ height: 600, width: "100%" }}>
          <DataGrid
            rows={filteredProducts}
            columns={columns}
            getRowId={(row) => String(row.id)}
            checkboxSelection
            selectionModel={selectedIds}
            onSelectionModelChange={handleSelectionModelChange}
            pagination
            pageSize={pageSize}
            onPageSizeChange={(newPageSize) => setPageSize(newPageSize)}
            rowsPerPageOptions={[5, 10, 25, 50]}
            columnVisibilityModel={columnVisibilityModel}
          />
        </div>
      </Box>

      {/* ADD/EDIT DIALOG */}
      <ProductFormDialog
        open={dialogOpen}
        mode={dialogMode}
        initialData={selectedProduct}
        onClose={() => setDialogOpen(false)}
        onSubmit={handleFormSubmit}
      />

      {/* CUSTOMIZE COLUMNS DIALOG */}
      <CustomizeColumnsDialog
        open={customizeDialogOpen}
        onClose={() => setCustomizeDialogOpen(false)}
        columns={baseColumns}
        columnVisibilityModel={columnVisibilityModel}
        onColumnVisibilityChange={handleColumnVisibilityChange}
      />
    </Box>
  );
};

export default InternalProductList;
