import axios from "axios";
import { updateLastSent } from "./api";  // ‚úÖ Correctly imported once

const API_URL = "https://api.kit.com/v4/broadcasts";
const API_KEY = "kit_a0a183e8fd744ca557d96126e488ae22";

const headers = {
  Accept: "application/json",
  "Content-Type": "application/json",
  "X-Kit-Api-Key": API_KEY,
};

/**
 * ‚úÖ Build the HTML Email EXACTLY Like Python Code
 */
function buildPythonStyleEmail(product) {
  return `
    <div style='padding: 20px 0;'>
        <div style="text-align:center; margin-bottom: 60px;">
            <h2 style="font-size:18px; margin-bottom: 20px;">${product.title}</h2>
            <img src="${product.image_url}" alt="${product.title}"
                 style="max-width:300px; max-height:300px; width:auto; height:auto; object-fit:contain;
                        display:block; margin-left:auto; margin-right:auto; margin-top:40px; margin-bottom:40px;"/>
            
            ${product.amazon_url ? `<a href="${product.amazon_url}"
               style="background-color:#FF9900; color:white; padding:15px 25px; text-align:center;
                      text-decoration:none; display:inline-block; font-size:16px; margin:5px; border-radius:5px;">
               Amazon Link
            </a>` : ""}
            
            ${product.walmart_url ? `<a href="${product.walmart_url}"
               style="background-color:#0071CE; color:white; padding:15px 25px; text-align:center;
                      text-decoration:none; display:inline-block; font-size:16px; margin:5px; border-radius:5px;">
               Walmart Link
            </a>` : ""}
            
            <p style="font-size:18px; margin-top:40px; margin-bottom: 10px;">
              $${product.price} EA, MOQ ${product.moq}, ${product.qty} Available.
            </p>
            <p style="font-size:18px; margin-bottom: 10px;">ASIN: ${product.asin}</p>
            <p style="font-size:18px; margin-bottom: 10px;">FOB: ${product.fob}</p>
            <p style="font-size:18px; margin-bottom: 20px;">Lead Time: ${product.lead_time}</p>
        </div>
    </div>
  `;
}

/**
 * ‚úÖ Send Individual Emails & Ensure last_sent is updated correctly
 */
export async function sendIndividualEmails(selectedProducts) {
  if (!selectedProducts || selectedProducts.length === 0) {
    console.error("No products selected for individual email.");
    return;
  }

  for (const product of selectedProducts) {
    if (!product?.id) {
      console.error("Skipping product with no ID:", product);
      continue;
    }

    const localUrl = `http://127.0.0.1:8000/products/${product.id}/send-email/`;
    console.log(`üîπ Sending individual email via FastAPI: POST ${localUrl}`);

    try {
      const emailData = {
        subject: product.title,
        content: buildPythonStyleEmail(product),
        public: false,
        email_template_id: null
      };

      console.log("\nüîç DEBUG: Payload being sent to Kit API:");
      console.log(JSON.stringify(emailData, null, 2));

      const response = await fetch(localUrl, {
        method: "POST",
        headers: new Headers({
          "Accept": "application/json",
          "Content-Type": "application/json",
          "X-Kit-Api-Key": API_KEY
        }),
        body: JSON.stringify(emailData),
      });

      if (response.ok) {
        console.log(`‚úÖ Email sent for ID ${product.id}`);
        await updateLastSent(product.id);  // ‚úÖ Ensure last_sent updates
      } else {
        console.error(`‚ùå Failed to send email for ID ${product.id}:`, await response.text());
      }
    } catch (error) {
      console.error("‚ùå Network error sending single email:", error);
    }
  }
}

/**
 * ‚úÖ Send Group Email & Ensure last_sent is updated correctly
 */
export async function sendGroupEmail(selectedProducts) {
  if (!selectedProducts || selectedProducts.length === 0) {
    console.error("No products selected for group email.");
    return;
  }

  const productIds = selectedProducts.map((p) => p.id).filter(Boolean);
  if (!productIds.length) {
    console.error("Skipping group email, invalid product IDs.");
    return;
  }

  const localUrl = "http://127.0.0.1:8000/products/send-group-email/";
  console.log(`üîπ Sending group email via FastAPI: POST ${localUrl}`, productIds);

  const emailData = {
    subject: `Group Deal: ${selectedProducts.length} Products Available!`,
    content: selectedProducts.map(buildPythonStyleEmail).join("<hr>"), // ‚úÖ Ensures full HTML formatting
    public: false,
    email_template_id: null
  };

  try {
    console.log("\nüîç DEBUG: Payload being sent to Kit API:");
    console.log(JSON.stringify(emailData, null, 2));

    const response = await fetch(localUrl, {
      method: "POST",
      headers: {
        "Accept": "application/json",
        "Content-Type": "application/json",
        "X-Kit-Api-Key": API_KEY
      },
      body: JSON.stringify(emailData),
    });

    if (response.ok) {
      console.log(`‚úÖ Group email sent successfully.`);
      for (const productId of productIds) {
        await updateLastSent(productId);  // ‚úÖ Ensure last_sent updates for all products
      }
    } else {
      console.error("‚ùå Failed to send group email:", await response.text());
    }
  } catch (error) {
    console.error("‚ùå Network error sending group email:", error);
  }
}
