import React, { useState, useEffect } from "react";
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stack,
  Typography
} from "@mui/material";

/**
 * Props:
 *  - open: boolean (controls whether dialog is open)
 *  - mode: "add" or "edit"
 *  - initialData: the product to edit (if mode === "edit")
 *  - onClose: callback when dialog is closed
 *  - onSubmit: callback when the user clicks "Save"
 */
const ProductFormDialog = ({
  open,
  mode = "add",
  initialData = {},
  onClose,
  onSubmit
}) => {
  // Keep a local state for all product fields, including our new timestamps
  const [formState, setFormState] = useState({
    title: "",
    category: "",
    vendor: "",
    price: "",
    moq: "",
    qty: "",
    upc: "",
    cost: "",
    lead_time: "",
    exp_date: "",
    fob: "",
    vendor_id: "",
    profit_moq: "",
    image_url: "",
    out_of_stock: false,
    walmart_url: "",
    ebay_url: "",
    amazon_url: "",
    asin: "",
    // ✅ Fields for timestamps
    offer_date: "",
    last_sent: "",
  });

  useEffect(() => {
    if (mode === "edit" && initialData) {
      setFormState({
        title: initialData.title || "",
        category: initialData.category || "",
        vendor: initialData.vendor || "",
        price: initialData.price || "",
        moq: initialData.moq || "",
        qty: initialData.qty || "",
        upc: initialData.upc || "",
        cost: initialData.cost || "",
        lead_time: initialData.lead_time ? String(initialData.lead_time) : "",
        exp_date: initialData.exp_date || "",
        fob: initialData.fob || "",
        vendor_id: initialData.vendor_id || "",
        profit_moq: initialData.profit_moq || "",
        image_url: initialData.image_url || "",
        out_of_stock: !!initialData.out_of_stock,
        walmart_url: initialData.walmart_url || "",
        ebay_url: initialData.ebay_url || "",
        amazon_url: initialData.amazon_url || "",
        asin: initialData.asin || "",
        // ✅ Timestamps if they exist
        offer_date: initialData.offer_date || "",
        last_sent: initialData.last_sent || "",
      });
    } else {
      // If adding a new product, reset the form
      setFormState({
        title: "",
        category: "",
        vendor: "",
        price: "",
        moq: "",
        qty: "",
        upc: "",
        cost: "",
        lead_time: "",
        exp_date: "",
        fob: "",
        vendor_id: "",
        profit_moq: "",
        image_url: "",
        out_of_stock: false,
        walmart_url: "",
        ebay_url: "",
        amazon_url: "",
        asin: "",
        offer_date: "",
        last_sent: "",
      });
    }
  }, [mode, initialData]);

  // Helper to parse user date strings into ISO or null
  function parseDateOrNull(dateStr) {
    if (!dateStr) return null; // If empty, we pass null
    const d = new Date(dateStr);
    if (isNaN(d.getTime())) {
      console.warn("Invalid date format, setting to null:", dateStr);
      return null;
    }
    return d.toISOString(); // e.g. "2025-02-27T18:30:00.000Z"
  }

  // Handle form changes
  const handleChange = (field, value) => {
    setFormState((prev) => ({ ...prev, [field]: value }));
  };

  // Handle save
  const handleSave = () => {
    // Convert numeric and date/time fields before submitting
    const productData = {
      ...formState,
      price: formState.price ? parseFloat(formState.price) : null,
      cost: formState.cost ? parseFloat(formState.cost) : null,
      moq: formState.moq ? parseInt(formState.moq, 10) : 0,
      qty: formState.qty ? parseInt(formState.qty, 10) : 0,
      profit_moq: formState.profit_moq ? parseFloat(formState.profit_moq) : null,

      // ✅ Convert date strings to valid ISO or null
      offer_date: parseDateOrNull(formState.offer_date),
      last_sent: parseDateOrNull(formState.last_sent),
    };
    onSubmit(productData);
  };

  return (
    <Dialog open={open} onClose={onClose} fullWidth maxWidth="md">
      <DialogTitle>{mode === "edit" ? "Edit Product" : "Add Product"}</DialogTitle>
      <DialogContent dividers>
        <Stack spacing={2}>
          <TextField
            label="Title"
            value={formState.title}
            onChange={(e) => handleChange("title", e.target.value)}
            required
          />
          <Stack direction="row" spacing={2}>
            <TextField
              label="Category"
              value={formState.category}
              onChange={(e) => handleChange("category", e.target.value)}
            />
            <TextField
              label="Vendor"
              value={formState.vendor}
              onChange={(e) => handleChange("vendor", e.target.value)}
            />
          </Stack>

          <Stack direction="row" spacing={2}>
            <TextField
              label="Price"
              type="number"
              value={formState.price}
              onChange={(e) => handleChange("price", e.target.value)}
            />
            <TextField
              label="Cost"
              type="number"
              value={formState.cost}
              onChange={(e) => handleChange("cost", e.target.value)}
            />
          </Stack>

          <Stack direction="row" spacing={2}>
            <TextField
              label="MOQ"
              type="number"
              value={formState.moq}
              onChange={(e) => handleChange("moq", e.target.value)}
            />
            <TextField
              label="Qty"
              type="number"
              value={formState.qty}
              onChange={(e) => handleChange("qty", e.target.value)}
            />
          </Stack>

          <Stack direction="row" spacing={2}>
            <TextField
              label="UPC"
              value={formState.upc}
              onChange={(e) => handleChange("upc", e.target.value)}
            />
            <TextField
              label="ASIN"
              value={formState.asin}
              onChange={(e) => handleChange("asin", e.target.value)}
            />
          </Stack>

          <Stack direction="row" spacing={2}>
            <TextField
              label="Lead Time"
              value={formState.lead_time}
              onChange={(e) => handleChange("lead_time", e.target.value)}
            />
            <TextField
              label="Exp Date"
              value={formState.exp_date}
              onChange={(e) => handleChange("exp_date", e.target.value)}
            />
          </Stack>

          <Stack direction="row" spacing={2}>
            <TextField
              label="FOB"
              value={formState.fob}
              onChange={(e) => handleChange("fob", e.target.value)}
            />
            <TextField
              label="Vendor ID"
              value={formState.vendor_id}
              onChange={(e) => handleChange("vendor_id", e.target.value)}
            />
          </Stack>

          <Stack direction="row" spacing={2}>
            <TextField
              label="Profit MOQ"
              type="number"
              value={formState.profit_moq}
              onChange={(e) => handleChange("profit_moq", e.target.value)}
            />
            <TextField
              label="Image URL"
              value={formState.image_url}
              onChange={(e) => handleChange("image_url", e.target.value)}
            />
          </Stack>

          <Stack direction="row" spacing={2}>
            <TextField
              label="Amazon URL"
              value={formState.amazon_url}
              onChange={(e) => handleChange("amazon_url", e.target.value)}
            />
            <TextField
              label="Walmart URL"
              value={formState.walmart_url}
              onChange={(e) => handleChange("walmart_url", e.target.value)}
            />
            <TextField
              label="eBay URL"
              value={formState.ebay_url}
              onChange={(e) => handleChange("ebay_url", e.target.value)}
            />
          </Stack>

          {/* For boolean fields: out_of_stock */}
          <FormControl>
            <Typography variant="body2">Out of Stock?</Typography>
            <Select
              value={formState.out_of_stock ? "yes" : "no"}
              onChange={(e) => handleChange("out_of_stock", e.target.value === "yes")}
            >
              <MenuItem value="no">No</MenuItem>
              <MenuItem value="yes">Yes</MenuItem>
            </Select>
          </FormControl>

          {/* ✅ NEW: Offer Date & Last Sent fields */}
          <Stack direction="row" spacing={2}>
            <TextField
              label="Offer Date (YYYY-MM-DD HH:MM)"
              value={formState.offer_date}
              onChange={(e) => handleChange("offer_date", e.target.value)}
              helperText="When this product was first (re)offered."
            />
            <TextField
              label="Last Sent (YYYY-MM-DD HH:MM)"
              value={formState.last_sent}
              onChange={(e) => handleChange("last_sent", e.target.value)}
              helperText="Most recent time an email was sent."
            />
          </Stack>
        </Stack>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button variant="contained" onClick={handleSave}>
          {mode === "edit" ? "Save Changes" : "Add Product"}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default ProductFormDialog;
