const API_BASE_URL = "http://127.0.0.1:8000";

// Helper function to handle the response from fetch
async function handleResponse(response) {
  if (!response.ok) {
    // Attempt to read error details from the response body
    const errorText = await response.text();
    throw new Error(`HTTP error ${response.status}: ${errorText}`);
  }
  return response.json();
}

// Fetch products from the FastAPI backend.
// Assumes the API returns an object with a 'products' key or an array of products.
export async function fetchProducts() {
  try {
    const response = await fetch(`${API_BASE_URL}/products/`);
    const data = await handleResponse(response);
    return data;
  } catch (error) {
    console.error("Error fetching products:", error);
    // Return an object with a products array to keep the structure consistent
    return { products: [] };
  }
}

// Create a new product in the FastAPI backend.
export async function createProduct(productData) {
  try {
    const response = await fetch(`${API_BASE_URL}/products/`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(productData),
    });
    return await handleResponse(response);
  } catch (error) {
    console.error("Error creating product:", error);
    return null;
  }
}
