import React, { useEffect, useState } from "react";
import { DataGrid } from "@mui/x-data-grid";
import {
  Box,
  Drawer,
  Typography,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stack
} from "@mui/material";
import ProductFormDialog from "./ProductFormDialog";
import CustomizeColumnsDialog from "./CustomizeColumnsDialog";
import { fetchProducts, updateProduct, createProduct } from "../api";

// IMPORTANT: Adjust these imports if your emailSender exports are named differently.
// Example: If your file exports { sendGroupEmail, sendIndividualEmails }, import them exactly:
import { sendGroupEmail, sendIndividualEmails } from "../emailSender"; 

// Width of your sidebar drawer
const drawerWidth = 300;

// Columns for the DataGrid. 
// (Keep your original columns or adjust as needed.)
const baseColumns = [
  { field: "title", headerName: "Title", width: 200 },
  { field: "amazon_url", headerName: "Amazon URL", width: 200 },
  { field: "asin", headerName: "ASIN", width: 120 },
  { field: "price", headerName: "Price", width: 120, type: "number" },
  { field: "moq", headerName: "MOQ", width: 100, type: "number" },
  { field: "qty", headerName: "Quantity", width: 100, type: "number" },
  { field: "upc", headerName: "UPC", width: 120 },
  { field: "cost", headerName: "Cost", width: 120, type: "number" },
  { field: "vendor", headerName: "Vendor", width: 150 },
  { field: "lead_time", headerName: "Lead Time", width: 150 },
  { field: "exp_date", headerName: "Exp Date", width: 150 },
  { field: "fob", headerName: "FOB", width: 150 },
  { field: "vendor_id", headerName: "Vendor ID", width: 150 },
  { field: "profit_moq", headerName: "Profit MOQ", width: 150, type: "number" },
  { field: "image_url", headerName: "Image URL", width: 200 },
  { field: "category", headerName: "Category", width: 150 },
  { field: "out_of_stock", headerName: "Out of Stock", width: 150, type: "boolean" },
  { field: "walmart_url", headerName: "Walmart URL", width: 200 },
  { field: "ebay_url", headerName: "eBay URL", width: 200 },
];

const InternalProductList = ({ onBack }) => {
  // ===================
  // 1. DATA & FILTER STATES
  // ===================
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  // Example filter states (if you have them; remove if not used)
  const [categoryFilter, setCategoryFilter] = useState("");
  const [vendorFilter, setVendorFilter] = useState("");
  // If you have a cost slider or range:
  // const [costRange, setCostRange] = useState([0, 1000000]);
  // If you have a stock filter:
  // const [stockFilter, setStockFilter] = useState("in");

  // For the DataGrid selection
  const [selectedIds, setSelectedIds] = useState([]);

  // Dialog states
  const [dialogOpen, setDialogOpen] = useState(false);
  const [customizeDialogOpen, setCustomizeDialogOpen] = useState(false);

  // ===================
  // 2. LOAD PRODUCTS ON MOUNT
  // ===================
  useEffect(() => {
    const loadProducts = async () => {
      try {
        const data = await fetchProducts();
        // Depending on your API, data might be { products: [...] } or just an array
        const productData = data.products || data;
        setProducts(productData);
        setFilteredProducts(productData); // Initial display
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };
    loadProducts();
  }, []);

  // ===================
  // 3. (OPTIONAL) APPLY FILTERS
  // ===================
  // If you have filtering logic, reapply it whenever users change filter states.
  // For example:
  /*
  const applyFilters = () => {
    let filtered = [...products];
    if (categoryFilter) {
      filtered = filtered.filter(p => p.category === categoryFilter);
    }
    if (vendorFilter) {
      filtered = filtered.filter(p => p.vendor === vendorFilter);
    }
    // If you track costRange:
    // filtered = filtered.filter(p => p.cost >= costRange[0] && p.cost <= costRange[1]);
    // If you track stockFilter:
    // if (stockFilter === "in") filtered = filtered.filter(p => !p.out_of_stock);
    // else if (stockFilter === "out") filtered = filtered.filter(p => p.out_of_stock);
    setFilteredProducts(filtered);
  };

  const resetFilters = () => {
    setCategoryFilter("");
    setVendorFilter("");
    // setCostRange([0, 1000000]);
    // setStockFilter("in");
    setFilteredProducts(products);
  };
  */

  // ===================
  // 4. HANDLE SELECTION
  // ===================
  const handleSelectionModelChange = (newSelection) => {
    console.log("Selected IDs:", newSelection);
    setSelectedIds(newSelection);
  };

  // ===================
  // 5. HANDLE EMAIL SENDING
  // ===================
  // We need full product objects, not just IDs.
  const handleSendIndividualEmails = () => {
    // Filter the "filteredProducts" to find objects matching the selected IDs
    const selectedProducts = filteredProducts.filter((p) =>
      selectedIds.includes(String(p.id))
    );
    // Now pass the product objects to "sendIndividualEmails"
    sendIndividualEmails(selectedProducts);
  };

  const handleSendGroupEmail = () => {
    const selectedProducts = filteredProducts.filter((p) =>
      selectedIds.includes(String(p.id))
    );
    sendGroupEmail(selectedProducts);
  };

  // ===================
  // 6. RENDER
  // ===================
  return (
    <Box sx={{ display: "flex" }}>
      {/* Side Drawer with Actions */}
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: "border-box" },
        }}
      >
        <Box sx={{ p: 2 }}>
          {/* Optional "Back" button */}
          {onBack && (
            <Button variant="contained" onClick={onBack} sx={{ mb: 2 }}>
              Back to Public View
            </Button>
          )}

          <Typography variant="h6" gutterBottom>
            Actions
          </Typography>

          <Stack spacing={1}>
            {/* If you have filter controls, they'd go here:
            <FormControl fullWidth>
              <InputLabel>Category</InputLabel>
              <Select
                value={categoryFilter}
                label="Category"
                onChange={(e) => setCategoryFilter(e.target.value)}
              >
                <MenuItem value="">All</MenuItem>
                <MenuItem value="Electronics">Electronics</MenuItem>
                ...
              </Select>
            </FormControl>
            <Button onClick={applyFilters}>Apply Filters</Button>
            <Button onClick={resetFilters}>Reset Filters</Button>
            */}
            
            {/* Add Product */}
            <Button
              variant="contained"
              color="secondary"
              onClick={() => setDialogOpen(true)}
            >
              Add Product
            </Button>

            {/* Customize Columns */}
            <Button
              variant="contained"
              color="info"
              onClick={() => setCustomizeDialogOpen(true)}
            >
              Customize Columns
            </Button>

            {/* Email Buttons */}
            <Button
              variant="contained"
              color="success"
              onClick={handleSendIndividualEmails}
            >
              Send Individual Emails
            </Button>
            <Button
              variant="contained"
              color="success"
              onClick={handleSendGroupEmail}
            >
              Send Group Email
            </Button>
          </Stack>
        </Box>
      </Drawer>

      {/* Main Content Area */}
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Internal Product List
        </Typography>

        <div style={{ height: 600, width: "100%" }}>
          <DataGrid
            rows={filteredProducts}
            columns={baseColumns}
            getRowId={(row) => String(row.id)}
            pageSize={10}
            // MUI was warning that 10 isn't in rowsPerPageOptions, so let's fix that:
            rowsPerPageOptions={[5, 10, 25, 50]}
            checkboxSelection
            selectionModel={selectedIds}
            onSelectionModelChange={handleSelectionModelChange}
          />
        </div>
      </Box>

      {/* Dialog to Add/Edit Product */}
      <ProductFormDialog
        open={dialogOpen}
        onClose={() => setDialogOpen(false)}
        onSubmit={(newProduct) => {
          createProduct(newProduct);
          setDialogOpen(false);
        }}
      />

      {/* Dialog to Customize Columns */}
      <CustomizeColumnsDialog
        open={customizeDialogOpen}
        onClose={() => setCustomizeDialogOpen(false)}
        columns={baseColumns} // Pass columns so we avoid .map() on undefined
      />
    </Box>
  );
};

export default InternalProductList;
