import React, { useState, useEffect } from 'react';
import { Dialog, DialogTitle, DialogContent, DialogActions, FormGroup, FormControlLabel, Checkbox, Button } from '@mui/material';

const CustomizeColumnsDialog = ({ open, onClose, columns, initialVisibility, onSave }) => {
  const [localVisibility, setLocalVisibility] = useState(initialVisibility);

  useEffect(() => {
    setLocalVisibility(initialVisibility);
  }, [initialVisibility, open]);

  const handleToggle = (field) => {
    setLocalVisibility((prev) => ({
      ...prev,
      [field]: !prev[field],
    }));
  };

  const handleSave = () => {
    onSave(localVisibility);
    onClose();
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Customize Columns</DialogTitle>
      <DialogContent>
        <FormGroup>
          {columns.map((col) =>
            col.field !== 'actions' && (
              <FormControlLabel
                key={col.field}
                control={
                  <Checkbox
                    checked={localVisibility[col.field] !== false}
                    onChange={() => handleToggle(col.field)}
                  />
                }
                label={col.headerName}
              />
            )
          )}
        </FormGroup>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button onClick={handleSave} variant="contained">Save</Button>
      </DialogActions>
    </Dialog>
  );
};

export default CustomizeColumnsDialog;
