import React, { useEffect, useState } from "react";
import { DataGrid } from "@mui/x-data-grid";
import {
  Box,
  Drawer,
  Typography,
  Button,
  Slider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stack
} from "@mui/material";
import ProductFormDialog from "./ProductFormDialog";
import CustomizeColumnsDialog from "./CustomizeColumnsDialog";
import { fetchProducts, updateProduct, createProduct } from "../api";

const drawerWidth = 300;

// ✅ Base columns (restored with Title first)
const baseColumns = [
  { field: "title", headerName: "Title", width: 200 },
  { field: "amazon_url", headerName: "Amazon URL", width: 200 },
  { field: "asin", headerName: "ASIN", width: 120 },
  { field: "price", headerName: "Price", width: 120, type: "number" },
  { field: "moq", headerName: "MOQ", width: 100, type: "number" },
  { field: "qty", headerName: "Quantity", width: 100, type: "number" },
  { field: "id", headerName: "Product ID", width: 120 }, // ✅ Ensure ID is displayed
];

// ✅ Ensure selection and email functionality is restored
const InternalProductList = ({ onBack }) => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedIds, setSelectedIds] = useState([]);
  
  // ✅ Fetch products on mount
  useEffect(() => {
    const loadProducts = async () => {
      try {
        const data = await fetchProducts();
        const productData = data.products || data;
        
        const enriched = productData.map((p, index) => ({
          ...p,
          id: String(p.id ?? p._id ?? (index + 1)), // ✅ Ensure ID is a string
        }));

        setProducts(enriched);
        setFilteredProducts(enriched.filter(p => !p.out_of_stock));
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };
    
    loadProducts();
  }, []);

  // ✅ Fix selection issue by ensuring IDs match
  const handleSelectionModelChange = (newSelection) => {
    console.log("New selection from DataGrid:", newSelection);
    setSelectedIds(newSelection.map(id => String(id)));
  };

  // ✅ Ensure selected IDs match when sending emails
  const handleSendEmail = (type) => {
    const selectedProducts = filteredProducts.filter(p =>
      p.id && selectedIds.includes(String(p.id))
    );

    if (selectedProducts.length === 0) {
      alert("No valid products selected for email.");
      return;
    }

    alert(`${type} Email triggered for ${selectedProducts.length} products.`);
  };

  return (
    <Box sx={{ display: "flex" }}>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: "border-box" }
        }}
      >
        <Box sx={{ p: 2 }}>
          {onBack && (
            <Button variant="contained" onClick={onBack} sx={{ mb: 2 }}>
              Back to Public View
            </Button>
          )}
          <Typography variant="h6" gutterBottom>Actions</Typography>
          <Stack spacing={1}>
            <Button variant="contained" color="secondary" onClick={() => setSelectedIds([])}>
              Clear Selection
            </Button>
            <Button variant="contained" color="success" onClick={() => handleSendEmail("Individual")}>
              Send Individual Emails
            </Button>
            <Button variant="contained" color="success" onClick={() => handleSendEmail("Group")}>
              Send Group Email
            </Button>
          </Stack>
        </Box>
      </Drawer>

      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h4" gutterBottom>Internal Product List</Typography>
        <div style={{ height: 600, width: "100%" }}>
          <DataGrid
            rows={filteredProducts}
            columns={baseColumns}
            getRowId={(row) => String(row.id)}
            pageSize={10}
            checkboxSelection
            selectionModel={selectedIds}
            onSelectionModelChange={handleSelectionModelChange}
          />
        </div>
      </Box>
    </Box>
  );
};

export default InternalProductList;
