import React, { useEffect, useState } from "react";
import { DataGrid } from "@mui/x-data-grid";
import {
  Box,
  Drawer,
  Typography,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stack,
} from "@mui/material";
import ProductFormDialog from "./ProductFormDialog";
import CustomizeColumnsDialog from "./CustomizeColumnsDialog";
import { fetchProducts, createProduct, updateProduct } from "../api";
// ✅ Import from the final "emailSender.js" above
import { sendIndividualEmails, sendGroupEmail } from "../emailSender";

const drawerWidth = 300;

// The same columns as you had
const baseColumns = [
  // ...
];

const allCategories = [ /* ... */ ];
const allVendors = [ /* ... */ ];

const InternalProductList = ({ onBack }) => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedIds, setSelectedIds] = useState([]);

  // Filters
  const [categoryFilter, setCategoryFilter] = useState("");
  const [vendorFilter, setVendorFilter] = useState("");

  // ✅ Default "in" for in-stock
  const [stockFilter, setStockFilter] = useState("in");

  // ✅ Default pageSize = 50
  const [pageSize, setPageSize] = useState(50);

  // Column visibility
  const [columnVisibilityModel, setColumnVisibilityModel] = useState({});

  // Dialog states
  const [dialogOpen, setDialogOpen] = useState(false);
  const [dialogMode, setDialogMode] = useState("add");
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Customize columns
  const [customizeDialogOpen, setCustomizeDialogOpen] = useState(false);

  useEffect(() => {
    const savedModel = localStorage.getItem("columnVisibilityModel");
    if (savedModel) {
      setColumnVisibilityModel(JSON.parse(savedModel));
    } else {
      const defaultModel = {};
      baseColumns.forEach((col) => {
        defaultModel[col.field] = true;
      });
      setColumnVisibilityModel(defaultModel);
    }
  }, []);

  // On mount, fetch
  useEffect(() => {
    loadProducts();
  }, []);

  async function loadProducts() {
    try {
      const data = await fetchProducts();
      const productData = data.products || data;
      setProducts(productData);
      setFilteredProducts(productData);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  }

  const handleColumnVisibilityChange = (newModel) => {
    setColumnVisibilityModel(newModel);
    localStorage.setItem("columnVisibilityModel", JSON.stringify(newModel));
  };

  // Filter
  const applyFilters = () => {
    let temp = [...products];

    if (categoryFilter) {
      temp = temp.filter((p) => (p.category || "").toLowerCase() === categoryFilter.toLowerCase());
    }
    if (vendorFilter) {
      temp = temp.filter((p) => (p.vendor || "").toLowerCase() === vendorFilter.toLowerCase());
    }
    if (stockFilter === "in") {
      temp = temp.filter((p) => p.out_of_stock === false);
    } else if (stockFilter === "out") {
      temp = temp.filter((p) => p.out_of_stock === true);
    }

    setFilteredProducts(temp);
  };

  const resetFilters = () => {
    setCategoryFilter("");
    setVendorFilter("");
    setStockFilter("all"); // or keep "in" if you prefer
    setFilteredProducts(products);
  };

  const handleSelectionModelChange = (newSelection) => {
    console.log("Selected IDs:", newSelection);
    setSelectedIds(newSelection);
  };

  const handleSendIndividualEmails = async () => {
    const selectedProducts = filteredProducts.filter((p) =>
      selectedIds.includes(String(p.id))
    );
    if (!selectedProducts.length) {
      alert("No products selected for individual email.");
      return;
    }
    await sendIndividualEmails(selectedProducts);
    loadProducts();
  };

  const handleSendGroupEmail = async () => {
    const selectedProducts = filteredProducts.filter((p) =>
      selectedIds.includes(String(p.id))
    );
    if (!selectedProducts.length) {
      alert("No products selected for group email.");
      return;
    }
    await sendGroupEmail(selectedProducts);
    loadProducts();
  };

  const handleAddClick = () => {
    setDialogMode("add");
    setSelectedProduct(null);
    setDialogOpen(true);
  };

  const handleEditClick = (product) => {
    setDialogMode("edit");
    setSelectedProduct(product);
    setDialogOpen(true);
  };

  const handleFormSubmit = async (formData) => {
    if (dialogMode === "add") {
      await createProduct(formData);
    } else {
      if (!selectedProduct?.id) {
        console.error("No product ID to update");
        return;
      }
      await updateProduct(selectedProduct.id, formData);
    }
    setDialogOpen(false);
    loadProducts();
  };

  // Overwrite actions col if needed
  const columns = baseColumns.map((col) => {
    if (col.field === "actions") {
      return {
        ...col,
        renderCell: (params) => {
          const handleClick = (e) => {
            e.stopPropagation();
            handleEditClick(params.row);
          };
          return (
            <Button variant="outlined" size="small" onClick={handleClick}>
              Edit
            </Button>
          );
        },
      };
    }
    return col;
  });

  return (
    <Box sx={{ display: "flex" }}>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: {
            width: drawerWidth,
            boxSizing: "border-box",
          },
        }}
      >
        <Box sx={{ p: 2 }}>
          {onBack && (
            <Button variant="contained" onClick={onBack} sx={{ mb: 2 }}>
              Back to Public View
            </Button>
          )}

          <Typography variant="h6" gutterBottom>
            Filters & Actions
          </Typography>

          <Stack spacing={1} sx={{ mb: 2 }}>
            {/* Category */}
            <FormControl size="small" fullWidth>
              <InputLabel>Category</InputLabel>
              <Select
                value={categoryFilter}
                label="Category"
                onChange={(e) => setCategoryFilter(e.target.value)}
              >
                <MenuItem value="">(All)</MenuItem>
                {allCategories.map((cat) => (
                  <MenuItem key={cat} value={cat}>
                    {cat}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            {/* Vendor */}
            <FormControl size="small" fullWidth>
              <InputLabel>Vendor</InputLabel>
              <Select
                value={vendorFilter}
                label="Vendor"
                onChange={(e) => setVendorFilter(e.target.value)}
              >
                <MenuItem value="">(All)</MenuItem>
                {allVendors.map((v) => (
                  <MenuItem key={v} value={v}>
                    {v}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            {/* Stock */}
            <FormControl size="small" fullWidth>
              <InputLabel>Stock Status</InputLabel>
              <Select
                value={stockFilter}
                label="Stock Status"
                onChange={(e) => setStockFilter(e.target.value)}
              >
                <MenuItem value="all">All</MenuItem>
                <MenuItem value="in">In Stock</MenuItem>
                <MenuItem value="out">Out of Stock</MenuItem>
              </Select>
            </FormControl>

            <Button variant="contained" onClick={applyFilters}>
              Apply Filters
            </Button>
            <Button variant="outlined" onClick={resetFilters}>
              Reset Filters
            </Button>
          </Stack>

          <Stack spacing={1}>
            <Button variant="contained" onClick={handleAddClick}>
              Add Product
            </Button>
            <Button
              variant="contained"
              color="info"
              onClick={() => setCustomizeDialogOpen(true)}
            >
              Customize Columns
            </Button>
            <Button variant="contained" color="success" onClick={handleSendIndividualEmails}>
              Send Individual Emails
            </Button>
            <Button variant="contained" color="success" onClick={handleSendGroupEmail}>
              Send Group Email
            </Button>
          </Stack>
        </Box>
      </Drawer>

      {/* Main Content */}
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Internal Product List
        </Typography>

        <div style={{ height: 600, width: "100%" }}>
          <DataGrid
            rows={filteredProducts}
            columns={columns}
            getRowId={(row) => String(row.id)}
            checkboxSelection
            selectionModel={selectedIds}
            onSelectionModelChange={setSelectedIds}
            pagination
            pageSize={pageSize}    // Default 50
            onPageSizeChange={setPageSize}
            rowsPerPageOptions={[5, 10, 25, 50, 100]}
            columnVisibilityModel={columnVisibilityModel}
          />
        </div>
      </Box>

      <ProductFormDialog
        open={dialogOpen}
        mode={dialogMode}
        initialData={selectedProduct}
        onClose={() => setDialogOpen(false)}
        onSubmit={handleFormSubmit}
      />

      <CustomizeColumnsDialog
        open={customizeDialogOpen}
        onClose={() => setCustomizeDialogOpen(false)}
        columns={baseColumns}
        columnVisibilityModel={columnVisibilityModel}
        onColumnVisibilityChange={handleColumnVisibilityChange}
      />
    </Box>
  );
};

export default InternalProductList;
